// src/pages/OnDutyPage.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, GeoJSON } from 'react-leaflet';
import { ArrowLeft, Siren } from 'lucide-react'; // <-- Import Siren icon

// Mock data for checkpoints
const checkpoints = [
  { id: 1, name: 'จุดตรวจที่ 1', position: [13.7563, 100.5018] },
  { id: 2, name: 'จุดตรวจที่ 2', position: [13.7600, 100.5025] },
  { id: 3, name: 'จุดตรวจที่ 3', position: [13.7580, 100.5050] },
  { id: 4, name: 'จุดตรวจที่ 4', position: [13.7550, 100.5030] },
];

// Sample GeoJSON data for the area of responsibility
const areaOfResponsibility = {
  type: "Feature",
  properties: {},
  geometry: {
    type: "Polygon",
    coordinates: [[
      [100.500, 13.754],
      [100.508, 13.754],
      [100.508, 13.762],
      [100.500, 13.762],
      [100.500, 13.754]
    ]]
  }
};

// Style for the GeoJSON layer
const areaStyle = {
  fillColor: "#3b82f6",
  fillOpacity: 0.2,
  color: "#1e40af",
  weight: 2
};

const OnDutyPage = () => {
  const defaultPosition = [13.758, 100.504]; // Centered in the polygon

  const handleGoBack = () => {
    window.history.back();
  };

  const handleSOS = () => {
    // In a real application, this would trigger a call to a backend service.
    // For now, we just show an alert.
    alert("ส่งคำขอความช่วยเหลือฉุกเฉินแล้ว! เจ้าหน้าที่กำลังไปที่ตำแหน่งของคุณ");
  };

  return (
    <div className="fixed inset-0 bg-white z-[1000]">
      <header className="absolute top-0 left-0 right-0 bg-white/80 backdrop-blur-md shadow-sm z-[1001]">
        <div className="flex items-center justify-between p-4 border-b border-slate-200">
          <button onClick={handleGoBack} className="p-2 rounded-full hover:bg-slate-100">
            <ArrowLeft className="w-6 h-6 text-slate-700" />
          </button>
          <h1 className="text-xl font-bold text-slate-800">หน้าสำหรับเจ้าหน้าที่เข้าเวร</h1>
          <div className="w-10"></div> {/* Spacer */}
        </div>
      </header>
      <div className="pt-[70px] h-full">
        <MapContainer center={defaultPosition} zoom={15} style={{ height: '100%', width: '100%' }}>
          <TileLayer
            url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
          />

          {/* Area of Responsibility Overlay */}
          <GeoJSON data={areaOfResponsibility} style={areaStyle} />

          {/* Checkpoint Markers */}
          {checkpoints.map(point => (
            <Marker key={point.id} position={point.position}>
              <Popup>
                {point.name}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* SOS Button */}
      <div className="absolute bottom-8 left-1/2 -translate-x-1/2 z-[1001]">
        <button 
          onClick={handleSOS}
          className="flex flex-col items-center justify-center w-20 h-20 bg-red-600 text-white rounded-full shadow-2xl transform active:scale-90 transition-transform duration-200 animate-pulse"
        >
          <Siren className="w-8 h-8" />
          <span className="text-sm font-bold mt-1">SOS</span>
        </button>
      </div>
    </div>
  );
};

export default OnDutyPage;
