// Unit Structure Modal Component
const UnitStructureModal = ({ isOpen, editingItem, formData, setFormData, availableOfficers, onSave, onClose, saving }) => {
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    if (!formData.position_number) {
      newErrors.position_number = 'กรุณาเลือกเจ้าหน้าที่';
    }

    if (!formData.unit_name.trim()) {
      newErrors.unit_name = 'กรุณาระบุชื่อส่วนงาน';
    }

    if (!formData.unit_number.trim()) {
      newErrors.unit_number = 'กรุณาระบุหมายเลขส่วนงาน';
    } else if (!/^[a-zA-Z]{2}\d{2}$/.test(formData.unit_number)) {
      newErrors.unit_number = 'รูปแบบต้องเป็น 2 ตัวอักษร + 2 ตัวเลข (เช่น cs01)';
    }

    if (!Number.isInteger(Number(formData.seniority_order)) || Number(formData.seniority_order) < 1) {
      newErrors.seniority_order = 'ลำดับอาวุโสต้องเป็นตัวเลขที่มากกว่า 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validateForm()) {
      onSave();
    }
  };

  const selectedOfficer = availableOfficers.find(officer => officer.position_number === formData.position_number);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl mx-4 max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b">
          <h3 className="text-lg font-semibold text-gray-900">
            {editingItem ? 'แก้ไขโครงสร้างส่วนงาน' : 'เพิ่มบุคลากรใหม่'}
          </h3>
        </div>

        <div className="p-6 space-y-6">
          {/* เลือกเจ้าหน้าที่ */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              เจ้าหน้าที่ <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.position_number}
              onChange={(e) => setFormData(prev => ({ ...prev, position_number: e.target.value }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.position_number ? 'border-red-300' : 'border-gray-300'
              }`}
              disabled={editingItem}
            >
              <option value="">เลือกเจ้าหน้าที่</option>
              {editingItem ? (
                <option value={editingItem.position_number}>
                  {editingItem.full_name} (เลขตำแหน่ง: {editingItem.position_number})
                </option>
              ) : (
                availableOfficers.map(officer => (
                  <option key={officer.position_number} value={officer.position_number}>
                    {officer.full_name} (เลขตำแหน่ง: {officer.position_number})
                  </option>
                ))
              )}
            </select>
            {errors.position_number && (
              <p className="mt-1 text-sm text-red-600">{errors.position_number}</p>
            )}
            {selectedOfficer && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Check className="w-4 h-4 text-blue-600" />
                  <span className="text-sm text-blue-800">
                    เลือก: {selectedOfficer.full_name} (ตำแหน่ง: {selectedOfficer.rank})
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* ชื่อส่วนงาน */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ชื่อส่วนงาน <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formData.unit_name}
              onChange={(e) => setFormData(prev => ({ ...prev, unit_name: e.target.value }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.unit_name ? 'border-red-300' : 'border-gray-300'
              }`}
              placeholder="เช่น ส่วนรักษาความปลอดภัย"
            />
            {errors.unit_name && (
              <p className="mt-1 text-sm text-red-600">{errors.unit_name}</p>
            )}
          </div>

          {/* หมายเลขส่วนงาน */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              หมายเลขส่วนงาน <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formData.unit_number}
              onChange={(e) => setFormData(prev => ({ ...prev, unit_number: e.target.value.toLowerCase() }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.unit_number ? 'border-red-300' : 'border-gray-300'
              }`}
              placeholder="เช่น cs01"
              maxLength={4}
            />
            {errors.unit_number && (
              <p className="mt-1 text-sm text-red-600">{errors.unit_number}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              รูปแบบ: 2 ตัวอักษร + 2 ตัวเลข (เช่น cs01, ad02)
            </p>
          </div>

          {/* บทบาท */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              บทบาท <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.role}
              onChange={(e) => {
                const newRole = e.target.value;
                setFormData(prev => ({ 
                  ...prev, 
                  role: newRole,
                  seniority_order: newRole === 'supervisor' ? 1 : prev.seniority_order
                }));
              }}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              <option value="member">เจ้าหน้าที่</option>
              <option value="supervisor">หัวหน้าส่วน</option>
              <option value="director">ผู้อำนวยการศูนย์</option>
            </select>

            {formData.role === 'director' && (
              <div className="mt-2 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <div className="flex items-start space-x-2">
                  <AlertTriangle className="w-4 h-4 text-yellow-600 mt-0.5" />
                  <span className="text-sm text-yellow-800">
                    ผู้อำนวยการศูนย์สามารถมีได้เพียงคนเดียวในระบบ
                  </span>
                </div>
              </div>
            )}

            {formData.role === 'supervisor' && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <div className="flex items-start space-x-2">
                  <AlertTriangle className="w-4 h-4 text-blue-600 mt-0.5" />
                  <span className="text-sm text-blue-800">
                    หัวหน้าส่วนจะมีลำดับอาวุโสเป็น 1 อัตโนมัติ และแต่ละส่วนงานสามารถมีได้เพียงคนเดียว
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* ลำดับอาวุโส */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ลำดับอาวุโส <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              min="1"
              value={formData.seniority_order}
              onChange={(e) => setFormData(prev => ({ ...prev, seniority_order: parseInt(e.target.value) || 1 }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.seniority_order ? 'border-red-300' : 'border-gray-300'
              }`}
              disabled={formData.role === 'supervisor'}
            />
            {errors.seniority_order && (
              <p className="mt-1 text-sm text-red-600">{errors.seniority_order}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              หัวหน้าส่วนจะมีลำดับ 1 เสมอ สำหรับเจ้าหน้าที่ให้กำหนดลำดับตั้งแต่ 2 เป็นต้นไป
            </p>
          </div>
        </div>

        <div className="p-6 border-t bg-gray-50 flex justify-end space-x-3">
          <button
            onClick={onClose}
            disabled={saving}
            className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50"
          >
            ยกเลิก
          </button>
          <button
            onClick={handleSave}
            disabled={saving}
            className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {saving ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                กำลังบันทึก...
              </>
            ) : (
              <>
                <Save className="w-4 h-4 mr-2" />
                บันทึก
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ShiftWorkPage;
