import {
  Calendar as CalendarIcon,
  Plus,
  ChevronLeft,
  ChevronRight,
  Filter,
  Search,
  Edit,
  Trash2,
  Save,
  X,
  Clock,
  Users,
  Building,
  FileText,
  Settings,
  File, // <-- Import ไอคอนใหม่
  Shield // <-- Import ไอคอนใหม่
} from 'lucide-react';
import { CONFIG } from '../config';

// --- สร้าง Component สำหรับ Tab เวรรักษาการ ---
const ShiftCalendarView = ({
    currentDate,
    schedules,
    officers,
    shiftTypes,
    units,
    filters,
    setFilters,
    navigateMonth,
    goToToday,
    openAddModal,
    openEditModal
}) => {
    const getDaysInMonth = (date) => {
        const year = date.getFullYear();
        const month = date.getMonth();
        const firstDay = new Date(year, month, 1);
        const lastDay = new Date(year, month + 1, 0);
        const daysInMonth = lastDay.getDate();
        const startingDayOfWeek = firstDay.getDay();
        const days = [];
        for (let i = 0; i < startingDayOfWeek; i++) {
            days.push(null);
        }
        for (let day = 1; day <= daysInMonth; day++) {
            days.push(new Date(year, month, day));
        }
        return days;
    };

    const getSchedulesForDate = (date) => {
        if (!date) return [];
        const dateStr = date.toISOString().split('T')[0];
        return schedules.filter(schedule => schedule.duty_date === dateStr);
    };

    const formatDate = (date) => {
        return date.toLocaleDateString('th-TH', {
            year: 'numeric',
            month: 'long'
        });
    };

    return (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
            <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                        <button onClick={() => navigateMonth(-1)} className="p-2 hover:bg-gray-100 rounded-lg">
                            <ChevronLeft className="w-4 h-4" />
                        </button>
                        <h2 className="text-lg font-semibold text-gray-900 min-w-0 px-3">
                            {formatDate(currentDate)}
                        </h2>
                        <button onClick={() => navigateMonth(1)} className="p-2 hover:bg-gray-100 rounded-lg">
                            <ChevronRight className="w-4 h-4" />
                        </button>
                    </div>
                    <button onClick={goToToday} className="px-3 py-1 text-sm font-medium text-indigo-600 hover:bg-indigo-50 rounded-lg">
                        วันนี้
                    </button>
                </div>
            </div>

            <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-2">
                    <Filter className="w-4 h-4 text-gray-500" />
                    <select
                        value={filters.unit}
                        onChange={(e) => setFilters(prev => ({ ...prev, unit: e.target.value }))}
                        className="border border-gray-300 rounded-lg px-3 py-2 text-sm"
                    >
                        <option value="">ทุกส่วนงาน</option>
                        {units.map(unit => (
                            <option key={unit.id} value={unit.id}>{unit.unit_name}</option>
                        ))}
                    </select>
                </div>
                <div className="flex items-center space-x-2">
                    <Search className="w-4 h-4 text-gray-500" />
                    <input
                        type="text"
                        placeholder="ค้นหาเจ้าหน้าที่..."
                        value={filters.officer}
                        onChange={(e) => setFilters(prev => ({ ...prev, officer: e.target.value }))}
                        className="border border-gray-300 rounded-lg px-3 py-2 text-sm"
                    />
                </div>
            </div>

            <div className="grid grid-cols-7 gap-1">
                {['อา', 'จ', 'อ', 'พ', 'พฤ', 'ศ', 'ส'].map((day, index) => (
                    <div key={index} className="p-3 text-center text-sm font-medium text-gray-600 border-b">
                        {day}
                    </div>
                ))}
                {getDaysInMonth(currentDate).map((date, index) => (
                    <div key={index} className="min-h-[100px] border border-gray-200 p-2">
                        {date && (
                            <>
                                <div className="text-sm font-medium text-gray-900 mb-2">{date.getDate()}</div>
                                <div className="space-y-1">
                                    {getSchedulesForDate(date).map((schedule) => (
                                        <div
                                            key={schedule.id}
                                            onClick={() => openEditModal(schedule)}
                                            className={`text-xs p-1 rounded cursor-pointer hover:opacity-80 ${schedule.shift_category === 'In-hours' ? 'bg-blue-100 text-blue-800' : 'bg-indigo-100 text-indigo-800'}`}
                                        >
                                            <div className="font-medium truncate">{schedule.shift_type_name}</div>
                                            <div className="truncate">{schedule.officer_names}</div>
                                            {schedule.unit_name && <div className="truncate text-xs opacity-75">{schedule.unit_name}</div>}
                                        </div>
                                    ))}
                                </div>
                                {getSchedulesForDate(date).length === 0 && (
                                    <button onClick={() => openAddModal(date)} className="w-full h-8 text-xs text-gray-400 hover:text-gray-600 hover:bg-gray-50 rounded flex items-center justify-center">
                                        + เพิ่มเวร
                                    </button>
                                )}
                            </>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

// --- สร้าง Component สำหรับ Tab จัดการเอกสาร ---
const DocumentManagementView = () => {
    return (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 text-center min-h-[400px] flex flex-col justify-center items-center">
            <FileText className="w-16 h-16 text-slate-300 mb-4" />
            <h2 className="text-xl font-semibold text-slate-800">จัดการเอกสาร</h2>
            <p className="text-slate-500 mt-2">🚧 ส่วนนี้ยังอยู่ระหว่างการพัฒนา 🚧</p>
        </div>
    );
};


// --- Component หลัก: ShiftWorkPage ---
const ShiftWorkPage = () => {
  const [activeTab, setActiveTab] = useState('shifts'); 
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSchedule, setSelectedSchedule] = useState(null);
  const [schedules, setSchedules] = useState([]);
  const [officers, setOfficers] = useState([]);
  const [shiftTypes, setShiftTypes] = useState([]);
  const [units, setUnits] = useState([]);
  const [formData, setFormData] = useState({ duty_date: '', shift_type_id: '', unit_id: '', officer_ids: [], notes: '' });
  const [filters, setFilters] = useState({ unit: '', officer: '' });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      await Promise.all([
        loadOfficers(),
        loadShiftTypes(),
        loadUnits(),
        loadSchedules()
      ]);
    } catch (error) {
      console.error('Error loading initial data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadSchedules = async () => {
    try {
      const month = currentDate.getMonth() + 1;
      const year = currentDate.getFullYear();
      
      // --- ส่วนที่ต้องแก้ไข ---
      const response = await fetch(`${CONFIG.API_ENDPOINTS.schedules}?action=get_all_schedules&month=${month}&year=${year}`);
      if (!response.ok) {
          throw new Error('Network response was not ok');
      }
      const data = await response.json();
      // สมมติว่า API คืนค่าเป็น { success: true, data: [...] }
      if (data.success) {
          setSchedules(data.data); 
      } else {
          console.error('Failed to fetch schedules:', data.message);
      }

    } catch (error) {
      console.error('Error loading schedules:', error);
    }
  };

  const loadOfficers = async () => {
    try {
      const response = await fetch(CONFIG.API_ENDPOINTS.officers);
      if (!response.ok) { // ตรวจสอบสถานะ HTTP ก่อน
          throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      
      // --- จุดแก้ไขที่สำคัญ ---
      // ตรวจสอบว่า data.data มีอยู่จริง และเป็น Array หรือไม่
      if (data && data.success && Array.isArray(data.data)) {
        setOfficers(data.data);
      } else {
        // ถ้าข้อมูลไม่ถูกต้อง ให้คงค่าเดิม (empty array) และแสดง error
        console.error('Failed to load officers or data is not in expected format:', data);
        setOfficers([]); // ตั้งค่าเป็น Array ว่างเสมอ เพื่อป้องกัน .map error
      }
      // --- จบจุดแก้ไข ---

    } catch (error) {
      console.error('Error loading officers:', error);
      setOfficers([]); // ถ้าเกิด Error ระหว่าง fetch ให้ตั้งค่าเป็น Array ว่าง
    }
  };

  const loadShiftTypes = async () => {
    try {
      const response = await fetch(`${CONFIG.API_ENDPOINTS.schedules}?action=get_all_shifttypes`); // <-- แก้ 1
      const data = await response.json();
      setShiftTypes(data); // <-- แก้ 2: ใช้ setShiftTypes (API นี้คืนค่าเป็น Array ตรงๆ)
    } catch (error) {
      console.error('Error loading shift types:', error);
    }
  };

  const loadUnits = async () => {
    try {
      const response = await fetch(`${CONFIG.API_ENDPOINTS.schedules}?action=get_all_units`);
      if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();

      // ตรวจสอบว่าเป็น Array หรือไม่
      if (Array.isArray(data)) {
        setUnits(data);
      } else {
        console.error('Failed to load units or data is not an array:', data);
        setUnits([]); // ป้องกัน Error
      }
    } catch (error) {
      console.error('Error loading units:', error);
      setUnits([]); // ป้องกัน Error
    }
  };

  // Calendar navigation
  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const openAddModal = (date = null) => {
    setFormData({
      duty_date: date ? date.toISOString().split('T')[0] : '',
      shift_type_id: '',
      unit_id: '',
      officer_ids: [],
      notes: ''
    });
    setShowAddModal(true);
  };

  const openEditModal = (schedule) => {
    setSelectedSchedule(schedule);
    setFormData({
      duty_date: schedule.duty_date,
      shift_type_id: schedule.shift_type_id,
      unit_id: schedule.unit_id || '',
      officer_ids: schedule.officer_ids || [],
      notes: schedule.notes || ''
    });
    setShowEditModal(true);
  };

  const closeModals = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setSelectedSchedule(null);
    setFormData({
      duty_date: '',
      shift_type_id: '',
      unit_id: '',
      officer_ids: [],
      notes: ''
    });
  };

  // Form handlers
  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleOfficerSelect = (officerId) => {
    setFormData(prev => ({
      ...prev,
      officer_ids: prev.officer_ids.includes(officerId)
        ? prev.officer_ids.filter(id => id !== officerId)
        : [...prev.officer_ids, officerId]
    }));
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      const payload = {
        action: selectedSchedule ? 'update_schedule' : 'create_schedule',
        ...formData,
        ...(selectedSchedule && { schedule_id: selectedSchedule.id })
      };

      const response = await fetch(CONFIG.API_ENDPOINTS.schedules, { // <-- แก้ 1: ใช้ Endpoint ที่ถูกต้อง
        method: 'POST', // <-- แก้ 2: กำหนด method เป็น POST
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload) // <-- แก้ 3: ส่งข้อมูล payload ใน body
      });

      const result = await response.json();
      if (!result.success) {
        throw new Error(result.message);
      }
      
      await loadSchedules(); // <-- แก้ 4: โหลดข้อมูลใหม่เพื่อให้ปฏิทินอัปเดต
      closeModals();
    } catch (error) {
      console.error('Error saving schedule:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!selectedSchedule) return;
    if (window.confirm('คุณแน่ใจหรือไม่ที่จะลบตารางเวรนี้?')) {
      try {
        setSaving(true);
        const payload = {
          action: 'delete_schedule',
          schedule_id: selectedSchedule.id
        };

        const response = await fetch(CONFIG.API_ENDPOINTS.schedules, { // <-- แก้ 1
          method: 'POST', // <-- แก้ 2
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload) // <-- แก้ 3
        });

        const result = await response.json();
        if (!result.success) {
          throw new Error(result.message);
        }

        await loadSchedules(); // <-- แก้ 4: โหลดข้อมูลใหม่
        closeModals();
      } catch (error) {
        console.error('Error deleting schedule:', error);
      } finally {
        setSaving(false);
      }
    }
  };

  // Calendar rendering helpers
  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();
    
    const days = [];
    
    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }
    
    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }
    
    return days;
  };

  const getSchedulesForDate = (date) => {
    if (!date) return [];
    const dateStr = date.toISOString().split('T')[0];
    return schedules.filter(schedule => schedule.duty_date === dateStr);
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long'
    });
  };

  // Get selected shift type for conditional unit display
  const selectedShiftType = shiftTypes.find(type => type.id === parseInt(formData.shift_type_id));
  const showUnitField = selectedShiftType?.shift_category === 'In-hours';

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">งานและเวร</h1>
          <p className="text-sm text-gray-600 mt-1">จัดการตารางเวรและมอบหมายงาน</p>
        </div>
        <div className="flex items-center space-x-3">
          <button
            onClick={() => setShowAdminPanel(true)}
            className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-lg text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 transition-colors"
          >
            <Settings className="w-4 h-4 mr-2" />
            จัดการ
          </button>
          <button
            onClick={() => openAddModal()}
            className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 transition-colors"
          >
            <Plus className="w-4 h-4 mr-2" />
            เพิ่มเวร
          </button>
        </div>
      </div>

      {/* Calendar Controls */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <div className="flex items-center justify-between mb-4">
          {/* Date Navigation */}
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-1">
              <button
                onClick={() => navigateMonth(-1)}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ChevronLeft className="w-4 h-4" />
              </button>
              <h2 className="text-lg font-semibold text-gray-900 min-w-0 px-3">
                {formatDate(currentDate)}
              </h2>
              <button
                onClick={() => navigateMonth(1)}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ChevronRight className="w-4 h-4" />
              </button>
            </div>
            <button
              onClick={goToToday}
              className="px-3 py-1 text-sm font-medium text-indigo-600 hover:bg-indigo-50 rounded-lg transition-colors"
            >
              วันนี้
            </button>
          </div>

          {/* View Controls */}
          <div className="flex items-center space-x-2">
            <div className="flex bg-gray-100 rounded-lg p-1">
              {['month', 'week', 'day'].map((viewType) => (
                <button
                  key={viewType}
                  onClick={() => setView(viewType)}
                  className={`px-3 py-1 text-sm font-medium rounded-md transition-colors ${
                    view === viewType
                      ? 'bg-white text-gray-900 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  {viewType === 'month' ? 'เดือน' : viewType === 'week' ? 'สัปดาห์' : 'วัน'}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="flex items-center space-x-4 mb-4">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-gray-500" />
            <select
              value={filters.unit}
              onChange={(e) => setFilters(prev => ({ ...prev, unit: e.target.value }))}
              className="border border-gray-300 rounded-lg px-3 py-2 text-sm focus:ring-indigo-500 focus:border-indigo-500"
            >
              <option value="">ทุกส่วนงาน</option>
              {units.map(unit => (
                <option key={unit.id} value={unit.id}>{unit.unit_name}</option>
              ))}
            </select>
          </div>
          
          <div className="flex items-center space-x-2">
            <Search className="w-4 h-4 text-gray-500" />
            <input
              type="text"
              placeholder="ค้นหาเจ้าหน้าที่..."
              value={filters.officer}
              onChange={(e) => setFilters(prev => ({ ...prev, officer: e.target.value }))}
              className="border border-gray-300 rounded-lg px-3 py-2 text-sm focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>
        </div>

        {/* Calendar Grid */}
        <div className="grid grid-cols-7 gap-1">
          {/* Week days header */}
          {['อา', 'จ', 'อ', 'พ', 'พฤ', 'ศ', 'ส'].map((day, index) => (
            <div key={index} className="p-3 text-center text-sm font-medium text-gray-600 border-b">
              {day}
            </div>
          ))}
          
          {/* Calendar days */}
          {getDaysInMonth(currentDate).map((date, index) => (
            <div key={index} className="min-h-[100px] border border-gray-200 p-2">
              {date && (
                <>
                  <div className="text-sm font-medium text-gray-900 mb-2">
                    {date.getDate()}
                  </div>
                  <div className="space-y-1">
                    {getSchedulesForDate(date).map((schedule) => (
                      <div
                        key={schedule.id}
                        onClick={() => openEditModal(schedule)}
                        className={`text-xs p-1 rounded cursor-pointer hover:opacity-80 transition-opacity ${
                          schedule.shift_category === 'In-hours' 
                            ? 'bg-blue-100 text-blue-800' 
                            : 'bg-indigo-100 text-indigo-800'
                        }`}
                      >
                        <div className="font-medium truncate">{schedule.shift_type_name}</div>
                        <div className="truncate">{schedule.officer_names}</div>
                        {schedule.unit_name && (
                          <div className="truncate text-xs opacity-75">{schedule.unit_name}</div>
                        )}
                      </div>
                    ))}
                  </div>
                  {getSchedulesForDate(date).length === 0 && (
                    <button
                      onClick={() => openAddModal(date)}
                      className="w-full h-8 text-xs text-gray-400 hover:text-gray-600 hover:bg-gray-50 rounded transition-colors flex items-center justify-center"
                    >
                      + เพิ่มเวร
                    </button>
                  )}
                </>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Add/Edit Modal */}
      {(showAddModal || showEditModal) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl mx-4">
            <div className="p-6 border-b">
              <h3 className="text-lg font-semibold text-gray-900">
                {showEditModal ? 'แก้ไขตารางเวร' : 'เพิ่มตารางเวรใหม่'}
              </h3>
            </div>
            
            <div className="p-6 space-y-4">
              {/* Date */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  วันที่
                </label>
                <input
                  type="date"
                  value={formData.duty_date}
                  onChange={(e) => handleInputChange('duty_date', e.target.value)}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-indigo-500 focus:border-indigo-500"
                />
              </div>

              {/* Shift Type */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  ประเภทเวร
                </label>
                <select
                  value={formData.shift_type_id}
                  onChange={(e) => handleInputChange('shift_type_id', e.target.value)}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-indigo-500 focus:border-indigo-500"
                >
                  <option value="">เลือกประเภทเวร</option>
                  {shiftTypes.map(type => (
                    <option key={type.id} value={type.id}>
                      {type.type_name} ({type.start_time} - {type.end_time})
                    </option>
                  ))}
                </select>
              </div>

              {/* Unit (conditional) */}
              {showUnitField && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    ส่วนงาน
                  </label>
                  <select
                    value={formData.unit_id}
                    onChange={(e) => handleInputChange('unit_id', e.target.value)}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    <option value="">เลือกส่วนงาน</option>
                    {units.map(unit => (
                      <option key={unit.id} value={unit.id}>
                        {unit.unit_name}
                      </option>
                    ))}
                  </select>
                </div>
              )}

              {/* Officers */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  เจ้าหน้าที่ผู้เข้าเวร
                </label>
                <div className="border border-gray-300 rounded-lg p-3 max-h-48 overflow-y-auto">
                  {officers.map(officer => (
                    <label key={officer.id} className="flex items-center py-2">
                      <input
                        type="checkbox"
                        checked={formData.officer_ids.includes(officer.id)}
                        onChange={() => handleOfficerSelect(officer.id)}
                        className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                      />
                      <span className="ml-3 text-sm text-gray-900">
                        {officer.prefix} {officer.firstname} {officer.lastname}
                      </span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Notes */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  หมายเหตุ
                </label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => handleInputChange('notes', e.target.value)}
                  rows={3}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-indigo-500 focus:border-indigo-500"
                  placeholder="รายละเอียดเพิ่มเติม..."
                />
              </div>
            </div>

            <div className="p-6 border-t bg-gray-50 flex justify-between">
              <div>
                {showEditModal && (
                  <button
                    onClick={handleDelete}
                    disabled={saving}
                    className="inline-flex items-center px-4 py-2 border border-red-300 text-sm font-medium rounded-lg text-red-700 bg-red-50 hover:bg-red-100 transition-colors disabled:opacity-50"
                  >
                    <Trash2 className="w-4 h-4 mr-2" />
                    ลบเวร
                  </button>
                )}
              </div>
              
              <div className="flex space-x-3">
                <button
                  onClick={closeModals}
                  disabled={saving}
                  className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 transition-colors disabled:opacity-50"
                >
                  ยกเลิก
                </button>
                <button
                  onClick={handleSave}
                  disabled={saving}
                  className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 transition-colors disabled:opacity-50"
                >
                  {saving ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      กำลังบันทึก...
                    </>
                  ) : (
                    <>
                      <Save className="w-4 h-4 mr-2" />
                      บันทึก
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShiftWorkPage;
