};

const ShiftWorkPage = () => {
  const [activeTab, setActiveTab] = useState('shifts'); 
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSchedule, setSelectedSchedule] = useState(null);
  const [schedules, setSchedules] = useState([]);
  const [officers, setOfficers] = useState([]);
  const [shiftTypes, setShiftTypes] = useState([]);
  const [units, setUnits] = useState([]);
  const [formData, setFormData] = useState({ duty_date: '', shift_type_id: '', unit_id: '', officer_ids: [], notes: '' });
  const [filters, setFilters] = useState({ unit: '', officer: '' });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  // --- ‚≠êÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç 1: ‡∏õ‡∏£‡∏±‡∏ö‡∏õ‡∏£‡∏∏‡∏á Logic ‡∏Å‡∏≤‡∏£‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÄ‡∏à‡πâ‡∏≤‡∏´‡∏ô‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥ ---
  useEffect(() => {
    if (!Array.isArray(shiftTypes) || !Array.isArray(officers)) return;
    
    const selectedShiftType = shiftTypes.find(type => type.id === parseInt(formData.shift_type_id));
    
    if (formData.unit_id && selectedShiftType?.shift_category === 'In-hours') {
        const officersInUnit = officers
            .filter(o => o.unit_id === parseInt(formData.unit_id))
            .map(o => o.id);

        setFormData(prev => ({
            ...prev,
            officer_ids: officersInUnit
        }));
    } else if (selectedShiftType?.shift_category !== 'In-hours') {
        setFormData(prev => ({
            ...prev,
            unit_id: ''
        }));
    }
  }, [formData.unit_id, formData.shift_type_id, officers, shiftTypes]);
  
  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  const loadInitialData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadOfficers(),
        loadShiftTypes(),
        loadUnits(),
        loadSchedules()
      ]);
    } catch (error) {
      console.error('Error loading initial data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadSchedules = async () => {
      try {
          const month = currentDate.getMonth() + 1;
          const year = currentDate.getFullYear();
          const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_schedules&month=${month}&year=${year}`);
          const data = await response.json();
          if (data.success) {
              setSchedules(data.data); 
          } else {
              console.error('Failed to fetch schedules:', data.message);
          }
      } catch (error) {
          console.error('Error loading schedules:', error);
      }
  };

  const loadOfficers = async () => {
      try {
        const response = await fetch(`${CONFIG.OFFICERS_API}?action=get_all_officers`);
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        if (data && data.success && Array.isArray(data.data)) {
          setOfficers(data.data);
        } else {
          console.error('Failed to load officers:', data);
          setOfficers([]);
        }
      } catch (error) {
        console.error('Error loading officers:', error);
        setOfficers([]);
      }
  };

  const loadShiftTypes = async () => {
      try {
          const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_shifttypes`);
          const data = await response.json();
          setShiftTypes(data);
      } catch (error) {
          console.error('Error loading shift types:', error);
      }
  };

  const loadUnits = async () => {
      try {
          const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_units`);
          const data = await response.json();
          if (Array.isArray(data)) {
              setUnits(data);
          } else {
              setUnits([]);
          }
      } catch (error) {
          console.error('Error loading units:', error);
          setUnits([]);
      }
  };

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(prev.getMonth() + direction);
      return newDate;
    });
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const openAddModal = (date = null) => {
    setFormData({
      duty_date: date ? date.toISOString().split('T')[0] : '',
      shift_type_id: '', unit_id: '', officer_ids: [], notes: ''
    });
    setShowAddModal(true);
  };

  const openEditModal = (schedule) => {
    setSelectedSchedule(schedule);
    setFormData({
      duty_date: schedule.duty_date,
      shift_type_id: schedule.shift_type_id,
      unit_id: schedule.unit_id || '',
      officer_ids: schedule.officer_ids || [],
      notes: schedule.notes || ''
    });
    setShowEditModal(true);
  };

  const closeModals = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setSelectedSchedule(null);
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleOfficerSelect = (officerId) => {
    setFormData(prev => ({
      ...prev,
      officer_ids: prev.officer_ids.includes(officerId)
        ? prev.officer_ids.filter(id => id !== officerId)
        : [...prev.officer_ids, officerId]
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const payload = {
        action: selectedSchedule ? 'update_schedule' : 'create_schedule',
        ...formData,
        ...(selectedSchedule && { schedule_id: selectedSchedule.id })
      };
      const response = await fetch(CONFIG.SCHEDULES_API, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (!result.success) throw new Error(result.message);
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error saving schedule:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!selectedSchedule || !window.confirm('‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ó‡∏µ‡πà‡∏à‡∏∞‡∏•‡∏ö‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏ß‡∏£‡∏ô‡∏µ‡πâ?')) return;
    setSaving(true);
    try {
      const payload = { action: 'delete_schedule', schedule_id: selectedSchedule.id };
      const response = await fetch(CONFIG.SCHEDULES_API, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (!result.success) throw new Error(result.message);
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error deleting schedule:', error);
    } finally {
      setSaving(false);
    }
  };

  // --- ‚≠êÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç 3: ‡∏™‡∏£‡πâ‡∏≤‡∏á state ‡∏ó‡∏µ‡πà‡∏Å‡∏£‡∏≠‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÅ‡∏•‡πâ‡∏ß‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Calendar ---
  const filteredSchedules = useMemo(() => {
    return schedules.filter(schedule => {
        const unitMatch = !filters.unit || schedule.unit_id === parseInt(filters.unit);
        const officerMatch = !filters.officer || 
            (schedule.officer_names && schedule.officer_names.toLowerCase().includes(filters.officer.toLowerCase()));
        return unitMatch && officerMatch;
    });
  }, [schedules, filters]);

  const selectedShiftType = shiftTypes.find(type => type.id === parseInt(formData.shift_type_id));
  const showUnitField = selectedShiftType?.shift_category === 'In-hours';

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">‡∏á‡∏≤‡∏ô‡πÅ‡∏•‡∏∞‡πÄ‡∏ß‡∏£</h1>
          <p className="text-sm text-gray-600 mt-1">‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏ß‡∏£‡πÅ‡∏•‡∏∞‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á</p>
        </div>
        <div className="flex items-center space-x-3">
          {activeTab === 'shifts' && (
            <button
                onClick={() => openAddModal()}
                className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700"
            >
                <Plus className="w-4 h-4 mr-2" />
                ‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ß‡∏£
            </button>
          )}
        </div>
      </div>

      <div className="border-b border-slate-200">
        <nav className="flex space-x-4">
            <button
                onClick={() => setActiveTab('shifts')}
                className={`flex items-center space-x-2 py-2 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${
                    activeTab === 'shifts' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
                }`}
            >
                <Shield className="w-4 h-4" />
                <span>‡πÄ‡∏ß‡∏£‡∏£‡∏±‡∏Å‡∏©‡∏≤‡∏Å‡∏≤‡∏£</span>
            </button>
            <button
                onClick={() => setActiveTab('documents')}
                className={`flex items-center space-x-2 py-2 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${
                    activeTab === 'documents' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
                }`}
            >
                <File className="w-4 h-4" />
                <span>‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£</span>
            </button>
        </nav>
      </div>
      
      <div>
        {activeTab === 'shifts' && (
            <ShiftCalendarView
                currentDate={currentDate}
                schedules={filteredSchedules} // <-- ‡∏™‡πà‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏Å‡∏£‡∏≠‡∏á‡πÅ‡∏•‡πâ‡∏ß‡πÄ‡∏Ç‡πâ‡∏≤‡πÑ‡∏õ
                officers={officers}
                shiftTypes={shiftTypes}
                units={units}
                filters={filters}
                setFilters={setFilters}
                navigateMonth={navigateMonth}
                goToToday={goToToday}
                openAddModal={openAddModal}
                openEditModal={openEditModal}
            />
        )}
        {activeTab === 'documents' && <DocumentManagementView />}
      </div>
      
      {(showAddModal || showEditModal) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl mx-4">
            <div className="p-6 border-b">
              <h3 className="text-lg font-semibold text-gray-900">
                {showEditModal ? '‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏ß‡∏£' : '‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏ß‡∏£‡πÉ‡∏´‡∏°‡πà'}
              </h3>
            </div>
            
            <div className="p-6 space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà</label>
                <input type="date" value={formData.duty_date} onChange={(e) => handleInputChange('duty_date', e.target.value)} className="w-full border border-gray-300 rounded-lg px-3 py-2"/>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÄ‡∏ß‡∏£</label>
                <select value={formData.shift_type_id} onChange={(e) => handleInputChange('shift_type_id', e.target.value)} className="w-full border border-gray-300 rounded-lg px-3 py-2">
                  <option value="">‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡πÄ‡∏ß‡∏£</option>
                  {/* --- ‚≠êÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç 2: ‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÑ‡∏≠‡∏Ñ‡∏≠‡∏ô Emoji ‡πÉ‡∏ô Dropdown --- */}
                  {shiftTypes.map(type => (
                    <option key={type.id} value={type.id}>
                      {getShiftEmoji(type.start_time, type.end_time, type.shift_category)} {type.type_name} ({type.start_time} - {type.end_time})
                    </option>
                  ))}
                </select>
              </div>
              {showUnitField && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">‡∏™‡πà‡∏ß‡∏ô‡∏á‡∏≤‡∏ô</label>
                  <select value={formData.unit_id} onChange={(e) => handleInputChange('unit_id', e.target.value)} className="w-full border border-gray-300 rounded-lg px-3 py-2">
                    <option value="">‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏™‡πà‡∏ß‡∏ô‡∏á‡∏≤‡∏ô</option>
                    {units.map(unit => (<option key={unit.id} value={unit.id}>{unit.unit_name}</option>))}
                  </select>
                </div>
              )}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">‡πÄ‡∏à‡πâ‡∏≤‡∏´‡∏ô‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏ú‡∏π‡πâ‡πÄ‡∏Ç‡πâ‡∏≤‡πÄ‡∏ß‡∏£</label>
                <div className="border border-gray-300 rounded-lg p-3 max-h-48 overflow-y-auto">
                  {/* --- ‚≠êÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç 4: ‡∏Å‡∏£‡∏≠‡∏á‡πÄ‡∏à‡πâ‡∏≤‡∏´‡∏ô‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏ó‡∏µ‡πà unit_id ‡πÑ‡∏°‡πà‡πÄ‡∏õ‡πá‡∏ô null --- */}
                  {officers.filter(officer => officer.unit_id !== null).map(officer => (
                    <label key={officer.id} className="flex items-center py-2">
                      <input type="checkbox" checked={formData.officer_ids.includes(officer.id)} onChange={() => handleOfficerSelect(officer.id)} className="h-4 w-4 text-indigo-600"/>
                      <span className="ml-3 text-sm text-gray-900">{officer.prefix} {officer.firstname} {officer.lastname}</span>
                    </label>
                  ))}
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏´‡∏ï‡∏∏</label>
                <textarea value={formData.notes} onChange={(e) => handleInputChange('notes', e.target.value)} rows={3} className="w-full border border-gray-300 rounded-lg px-3 py-2" placeholder="‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ï‡∏¥‡∏°..."/>
              </div>
            </div>

            <div className="p-6 border-t bg-gray-50 flex justify-between">
              <div>
                {showEditModal && (<button onClick={handleDelete} disabled={saving} className="inline-flex items-center px-4 py-2 border border-red-300 text-sm font-medium rounded-lg text-red-700 bg-red-50 hover:bg-red-100"><Trash2 className="w-4 h-4 mr-2" />‡∏•‡∏ö‡πÄ‡∏ß‡∏£</button>)}
              </div>
              <div className="flex space-x-3">
                <button onClick={closeModals} disabled={saving} className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50">‡∏¢‡∏Å‡πÄ‡∏•‡∏¥‡∏Å</button>
                <button onClick={handleSave} disabled={saving} className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700">
                  {saving ? (<><div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å...</>) : (<><Save className="w-4 h-4 mr-2" />‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å</>)}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShiftWorkPage;
