// src/layouts/AppLayout.js
import React, { useState, useEffect, useCallback } from "react";
import { Outlet, Link, useLocation, useNavigate } from "react-router-dom";
import {
  Bell,
  Settings,
  Users,
  ClipboardList,
  UserCircle2,
  MessageSquareText,
  Home,
  LogOut,
  X,
  ChevronRight,
  Shield,
  ShieldCheck,
  Menu,
  Search,
  ChevronLeft,
  Dot,
  Map // <-- Import Map icon
} from "lucide-react";
import logo from '../assets/logo.png';

export default function AppLayout({
  user,
  appName = "CourtMarshalConnect",
  onOpenSettings,
  onOpenNotifications,
  onLogout,
  children,
}) {
  const [showMobileSettings, setShowMobileSettings] = useState(false);
  const [isDesktop, setIsDesktop] = useState(window.innerWidth >= 1024);

  // Handle screen resize
  useEffect(() => {
    const handleResize = () => {
      const desktop = window.innerWidth >= 1024;
      setIsDesktop(desktop);
      if (desktop) {
        setShowMobileSettings(false);
      }
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Navigation items configuration
  // added memoization
  const navigationItems = [
    { 
      to: "/", 
      key: "home", 
      label: "หน้าหลัก", 
      icon: Home, 
      description: "ภาพรวม",
      badge: null
    },
    { 
      to: "/personnel", 
      key: "personnel", 
      label: "บุคลากร", 
      icon: Users, 
      description: "จัดการข้อมูลเจ้าหน้าที่",
      badge: null
    },
    { 
      to: "/tasks", 
      key: "tasks", 
      label: "งานและเวร", 
      icon: ClipboardList, 
      description: "จัดตารางและมอบหมายงาน",
      badge: null
    },
    { 
      to: "/chat", 
      key: "chat", 
      label: "แชทหน่วย", 
      icon: MessageSquareText, 
      description: "สื่อสารภายในทีม",
      badge: null
    },
    { 
      to: "/profile", 
      key: "profile", 
      label: "โปรไฟล์", 
      icon: UserCircle2, 
      description: "ข้อมูลส่วนตัว",
      badge: null
    },
  ];

  // Add admin menu if user is admin
  if (user && user.role === 'admin') {
    navigationItems.splice(4, 0, {
      to: "/admin",
      key: "admin",
      label: "แผงควบคุม",
      icon: ShieldCheck,
      description: "จัดการระบบสำหรับ Admin",
      badge: null
    });
  }

  const isActive = useCallback((to) => {
    const currentPath = window.location.pathname;
    return to === "/" ? currentPath === "/" : currentPath.startsWith(to);
  }, []);

  const navigate = useNavigate();
  const handleNavigation = useCallback((path) => {
    navigate(path);
    closeMobileMenus();
  }, [navigate]);

  const closeMobileMenus = () => {
    setShowMobileSettings(false);
  };

  // Floating Action Button for On-Duty Page
  const OnDutyFAB = () => (
    <button
      onClick={() => handleNavigation('/on-duty')}
      className="fixed bottom-24 right-6 lg:bottom-8 lg:right-8 bg-gradient-to-br from-green-500 to-teal-600 text-white p-4 rounded-full shadow-lg hover:scale-105 active:scale-95 transition-transform duration-200 z-50"
      aria-label="Go to On-Duty Page"
    >
      <Map className="w-6 h-6" />
    </button>
  );

  // Desktop Layout
  if (isDesktop) {
    return (
      <div className="min-h-screen w-full bg-gradient-to-br from-slate-50 via-blue-50/30 to-indigo-50/50">


        <div className="flex">
          {/* Desktop Sidebar - Fixed and Hover */}
          <aside className="fixed left-0 top-0 h-screen group bg-gradient-to-b from-slate-900 to-gray-900 border-r border-slate-700/60 shadow-2xl transition-all duration-300 ease-in-out w-20 hover:w-80 flex flex-col z-50">

            {/* Logo Section */}
            <div className="p-4 border-b border-slate-700/60">
              <div className="flex items-center justify-center group-hover:justify-start group-hover:space-x-3">
                <img src={logo} alt="App Logo" className="h-10 w-10 object-contain drop-shadow-lg flex-shrink-0" />
                <div className="opacity-0 group-hover:opacity-100 transition-all duration-300 overflow-hidden whitespace-nowrap min-w-0">
                  <h2 className="text-lg font-bold text-white" style={{ fontFamily: "'Orbitron', monospace" }}>
                    CMC
                  </h2>
                  <p className="text-xs text-slate-400">Court Marshal Connect</p>
                </div>
              </div>
            </div>

            {/* Navigation */}
            <nav className="flex-1 p-3 space-y-2 mt-2">
              {navigationItems.map((item) => (
                <Link
                  key={item.key}
                  to={item.to}
                  className={`w-full group/item flex items-center justify-center group-hover:justify-start group-hover:space-x-4 px-4 py-3 rounded-lg transition-all duration-200 text-left relative transform-gpu active:scale-[0.97] ${
                    isActive(item.to) 
                      ? 'bg-indigo-500/10 text-indigo-200' 
                      : 'text-slate-400 hover:bg-slate-800 hover:text-slate-100'
                  }`}
                >
                  {isActive(item.to) && (
                    <div className="absolute left-0 inset-y-2 w-1 bg-indigo-400 rounded-r-full"></div>
                  )}
                  <div className="relative flex-shrink-0">
                    <item.icon className={`w-5 h-5 transition-transform duration-200 group-hover/item:scale-110 ${isActive(item.to) ? 'text-indigo-300' : 'text-slate-500 group-hover/item:text-slate-300'}`} />
                    {item.badge && (
                      <span className="absolute -top-2 -right-2 w-4 h-4 bg-red-500 text-white text-xs rounded-full flex items-center justify-center font-bold">
                        {item.badge}
                      </span>
                    )}
                  </div>

                  <div className="opacity-0 group-hover:opacity-100 transition-all duration-300 overflow-hidden whitespace-nowrap min-w-0 flex-1">
                    <div className="font-semibold text-sm">{item.label}</div>
                    <div className={`text-xs ${isActive(item.to) ? 'text-indigo-300/70' : 'text-slate-500'}`}>
                      {item.description}
                    </div>
                  </div>

                  <ChevronRight className={`w-4 h-4 flex-shrink-0 opacity-0 group-hover:opacity-100 transition-all duration-300 ${
                    isActive(item.to) ? 'text-indigo-300' : 'text-slate-600'
                  }`} />

                  {/* Tooltip for collapsed state */}
                  <div className="absolute left-full ml-4 px-3 py-2 bg-gray-800 border border-slate-700 text-white text-sm rounded-lg opacity-0 invisible group-hover:invisible group/item-hover:opacity-100 group/item-hover:visible group-hover:opacity-0 transition-all duration-200 whitespace-nowrap z-50">
                    {item.label}
                    <div className="absolute top-1/2 -left-1 transform -translate-y-1/2 w-2 h-2 bg-gray-800 rotate-45"></div>
                  </div>
                </Link>
              ))}
            </nav>

            {/* Bottom Section */}
            <div className="mt-auto">
              {/* User Profile */}
              <div className="p-4 border-t border-slate-700/60">
                <div className="flex items-center justify-center group-hover:justify-start group-hover:space-x-3">
                  <div className="w-10 h-10 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-full flex items-center justify-center shadow-lg flex-shrink-0">
                      <UserCircle2 className="w-6 h-6 text-white" />
                  </div>
                  <div className="opacity-0 group-hover:opacity-100 transition-all duration-300 overflow-hidden whitespace-nowrap min-w-0">
                    <p className="text-sm font-semibold text-white truncate">
                        {(() => {
                          const prefix = user?.prefix || '';
                          const firstname = user?.firstname || '';
                          const lastname = user?.lastname || '';
                          const fullName = `${prefix} ${firstname} ${lastname}`.trim();
                          return fullName || 'ผู้ใช้งาน';
                        })()}
                    </p>
                    <p className="text-xs text-slate-400 truncate">{user?.position || "เจ้าหน้าที่"}</p>
                  </div>
                </div>
              </div>
              {/* Actions */}
              <div className="p-4 border-t border-slate-700/60 space-y-2">
                <button 
                  onClick={onOpenSettings}
                  className="w-full group/item flex items-center justify-center group-hover:justify-start group-hover:space-x-4 px-4 py-3 text-slate-300 hover:bg-slate-800 rounded-xl transition-all duration-200 relative"
                >
                  <Settings className="w-5 h-5 text-slate-400 flex-shrink-0" />
                  <span className="font-semibold text-sm opacity-0 group-hover:opacity-100 transition-all duration-300 overflow-hidden whitespace-nowrap group-hover:text-white">
                    ตั้งค่า
                  </span>
                  <div className="absolute left-full ml-2 px-3 py-2 bg-gray-900 text-white text-sm rounded-lg opacity-0 invisible group-hover:invisible group/item-hover:opacity-100 group/item-hover:visible group-hover:opacity-0 transition-all duration-200 whitespace-nowrap z-50">
                    ตั้งค่า
                    <div className="absolute top-1/2 -left-1 transform -translate-y-1/2 w-2 h-2 bg-gray-900 rotate-45"></div>
                  </div>
                </button>

                <button 
                  onClick={onLogout}
                  className="w-full group/item flex items-center justify-center group-hover:justify-start group-hover:space-x-4 px-4 py-3 text-red-500 hover:bg-red-500/10 rounded-xl transition-all duration-200 relative"
                >
                  <LogOut className="w-5 h-5 flex-shrink-0" />
                  <span className="font-semibold text-sm opacity-0 group-hover:opacity-100 transition-all duration-300 overflow-hidden whitespace-nowrap group-hover:text-red-400">
                    ออกจากระบบ
                  </span>
                  <div className="absolute left-full ml-2 px-3 py-2 bg-gray-900 text-white text-sm rounded-lg opacity-0 invisible group-hover:invisible group/item-hover:opacity-100 group/item-hover:visible group-hover:opacity-0 transition-all duration-200 whitespace-nowrap z-50">
                    ออกจากระบบ
                    <div className="absolute top-1/2 -left-1 transform -translate-y-1/2 w-2 h-2 bg-gray-900 rotate-45"></div>
                  </div>
                </button>
              </div>
            </div>
          </aside>

          {/* Main Content - with proper margin and padding */}
          <main className="flex-1 ml-20 p-6 overflow-y-auto h-screen">
            {children || <Outlet />}
          </main>

          <OnDutyFAB />
        </div>
      </div>
    );
  }

  // Mobile Layout
  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-slate-50 via-blue-50/30 to-indigo-50/50">
      {/* Mobile Header */}
      <header className="bg-white/90 backdrop-blur-md border-b border-slate-200/60 shadow-sm sticky top-0 z-50">
        <div className="px-4 py-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">

              <div className="flex items-center space-x-2">
                <img src={logo} alt="App Logo" className="h-8 w-8 object-contain drop-shadow-lg" />
                <div>
                  <h1 className="text-lg font-bold text-slate-900" style={{ fontFamily: "'Orbitron', monospace" }}>
                    CMC
                  </h1>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <button 
                onClick={onOpenNotifications}
                className="relative p-2 text-slate-600 hover:text-slate-900 hover:bg-slate-100 rounded-lg transition-colors"
              >
                <Bell className="w-5 h-5" />
              </button>

              <button
                onClick={() => setShowMobileSettings(true)}
                className="w-8 h-8 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-lg flex items-center justify-center"
              >
                <UserCircle2 className="w-5 h-5 text-white" />
              </button>
            </div>
          </div>
        </div>
      </header>



      {/* Mobile Settings Overlay */}
      {showMobileSettings && (
        <div className="fixed inset-0 bg-black/50 z-50" onClick={closeMobileMenus}>
          <div className="fixed inset-y-0 right-0 w-80 bg-white shadow-xl" onClick={(e) => e.stopPropagation()}>
            <div className="p-4 border-b border-slate-200">
              <div className="flex items-center justify-between">
                <h2 className="text-lg font-bold text-slate-900">โปรไฟล์</h2>
                <button
                  onClick={closeMobileMenus}
                  className="p-2 text-slate-500 hover:text-slate-700 hover:bg-slate-100 rounded-lg"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>
            </div>

            <div className="p-4">
              {/* User Info */}
              <div className="flex items-center space-x-4 p-4 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-xl">
                <div className="w-16 h-16 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl flex items-center justify-center">
                  <UserCircle2 className="w-8 h-8 text-white" />
                </div>
                <div>
                  <h3 className="font-bold text-slate-900">{user?.fullname || "ผู้ใช้งาน"}</h3>
                  <p className="text-sm text-slate-600">{user?.position || "เจ้าหน้าที่"}</p>
                  <p className="text-sm text-slate-500">{user?.phone || "ไม่ระบุ"}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Mobile Main Content */}
      <main className="p-4 pb-24 h-full">
        {children || <Outlet />}
      </main>

      {/* Mobile Bottom Navigation */}
      <nav className="fixed bottom-4 left-4 right-4 bg-white/95 backdrop-blur-md border border-slate-200/60 rounded-2xl shadow-lg px-4 py-2 z-40">
        <div className="flex items-center justify-around">
          {navigationItems.slice(0, 5).map((item) => (
            <Link
              key={item.key}
              to={item.to}
              className={`flex flex-col items-center space-y-1 p-2 rounded-xl transition-all duration-200 relative transform active:scale-95 ${
                isActive(item.to) ? 'text-indigo-600' : 'text-slate-500'
              }`} // added transition
            >
              <div className="relative">
                <item.icon className={`w-5 h-5 ${isActive(item.to) ? 'text-indigo-600' : 'text-slate-500'}`} />
                {item.badge && (
                  <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full flex items-center justify-center">
                    <Dot className="w-3 h-3 text-white" />
                  </span>
                )}
              </div>
              <span className={`text-xs font-medium ${isActive(item.to) ? 'text-indigo-600' : 'text-slate-500'}`}>
                {item.label}
              </span>
              {isActive(item.to) && (
                <div className="absolute -top-1 left-1/2 transform -translate-x-1/2 w-1 h-1 bg-indigo-600 rounded-full"></div>
              )}
            </Link>
          ))}
        </div>
      </nav>

      <OnDutyFAB />
    </div>
  );
}
