import React from "react";
import { Settings } from "lucide-react";

export default function Topbar({ 
  appName, 
  currentPage, 
  logo, 
  isMobile, 
  onSettingsClick,
  children 
}) {
  return (
    <header className={`w-full sticky top-0 z-50 bg-white/90 backdrop-blur-md border-b border-blue-100 shadow-xl px-4 py-3 flex items-center justify-between ${isMobile ? "rounded-b-2xl" : "rounded-none"}`}>
      <div className="flex items-center space-x-3">
        {isMobile ? (
          <>
            {logo ? (
              <img src={logo} alt="App Logo" className="h-8 w-8 object-contain drop-shadow-lg" />
            ) : (
              <div className="w-8 h-8 bg-blue-700 rounded-lg flex items-center justify-center">
                <div className="w-5 h-5 bg-yellow-400 rounded-sm"></div>
              </div>
            )}
            <div className="flex flex-col">
              <span className="text-lg font-bold text-blue-900 leading-tight">{appName}</span>
              {currentPage && (
                <span className="text-xs text-blue-600 leading-tight">{currentPage}</span>
              )}
            </div>
          </>
        ) : (
          <>
            {logo ? (
              <img src={logo} alt="App Logo" className="h-10 w-10 object-contain drop-shadow-lg" />
            ) : (
              <div className="w-10 h-10 bg-blue-700 rounded-lg flex items-center justify-center">
                <div className="w-6 h-6 bg-yellow-400 rounded-sm"></div>
              </div>
            )}
            <span className="text-2xl font-bold text-blue-900">{currentPage}</span>
          </>
        )}
      </div>
      <div className="flex items-center space-x-2">
        {children}
        {isMobile && onSettingsClick && (
          <button
            onClick={onSettingsClick}
            className="p-1.5 text-blue-700 hover:text-blue-900 hover:bg-blue-100 rounded-lg transition-colors"
            aria-label="ตั้งค่า"
          >
            <Settings className="w-5 h-5" />
          </button>
        )}
      </div>
    </header>
  );
}
