    { to: "/tasks", key: "tasks", label: "ตารางเวร", icon: CalendarCheck2, description: "จัดตารางงาน", badge: null },
    { to: "/chat", key: "chat", label: "แชท", icon: MessagesSquare, description: "สื่อสารทีม", badge: null },
