import React, { useState, useEffect, useMemo } from 'react';
import {
  Plus, ChevronLeft, ChevronRight, Filter, Search, Save, X, Sun, Moon, File, Shield, Building, User
} from 'lucide-react';
import { CONFIG } from '../config';

// --- Helper Function: getShiftIcon (แก้ไขให้แม่นยำขึ้น) ---
const getShiftIcon = (shiftType, className = "w-5 h-5 mr-3") => {
    if (!shiftType) return <Sun key="default-icon" className={`${className} text-gray-400`} />;
    
    // ตัดสินจาก shift_category ก่อนเป็นอันดับแรก เพราะแน่นอนที่สุด
    if (shiftType.shift_category === 'In-hours') {
        return <Sun key="in-hours-icon" className={`${className} text-yellow-600`} />;
    }
    if (shiftType.shift_category === 'Out-of-hours') {
        return <Moon key="out-hours-icon" className={`${className} text-indigo-600`} />;
    }
    
    // ถ้าไม่มี shift_category ให้ใช้เวลาเป็นตัวสำรอง
    if (shiftType.start_time) {
        const hour = parseInt(shiftType.start_time.split(':')[0], 10);
        if (hour >= 7 && hour < 19) {
            return <Sun key="day-icon" className={`${className} text-yellow-600`} />;
        }
    }
    return <Moon key="night-icon" className={`${className} text-indigo-600`} />;
};

// --- Helper Function: กำหนดสีปุ่มตามประเภทเวร ---
const getButtonColor = (type, isSelected) => {
    if (!isSelected) return 'bg-white border-gray-300 hover:bg-gray-50';
    
    // กำหนดสีตามประเภทเวร
    if (type.shift_category === 'In-hours') {
        return 'bg-yellow-50 border-yellow-500 ring-2 ring-yellow-200'; // เวรในเวลา = สีเหลือง
    } else if (type.shift_category === 'Out-of-hours') {
        return 'bg-indigo-50 border-indigo-500 ring-2 ring-indigo-200'; // เวรนอกเวลา = สีม่วงน้ำเงิน
    } else {
        return 'bg-blue-50 border-blue-500 ring-2 ring-blue-200'; // default = สีน้ำเงิน
    }
};

// --- Calendar View Component (แก้ไข key props) ---
const ShiftCalendarView = ({ currentDate, schedules, navigateMonth, goToToday, openAddModal, openEditModal, filters, setFilters, units, shiftTypes }) => {
    const getDaysInMonth = (date) => {
        const year = date.getFullYear();
        const month = date.getMonth();
        const firstDay = new Date(year, month, 1);
        const lastDay = new Date(year, month + 1, 0);
        const daysInMonth = lastDay.getDate();
        const startingDayOfWeek = firstDay.getDay();
        const days = [];
        for (let i = 0; i < startingDayOfWeek; i++) { days.push(null); }
        for (let day = 1; day <= daysInMonth; day++) { days.push(new Date(year, month, day));}
        return days;
    };
    
    const getSchedulesForDate = (date) => {
        if (!date) return [];
        const dateStr = date.toISOString().split('T')[0];
        return schedules.filter(schedule => schedule.duty_date === dateStr);
    };
    
    const formatDate = (date) => date.toLocaleString('th-TH', { year: 'numeric', month: 'long' });

    const getShiftTypeForSchedule = (schedule) => {
        return shiftTypes.find(st => st.id == schedule.shift_type_id) || { start_time: schedule.start_time, shift_category: schedule.shift_category };
    };

    return (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
            <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                        <button onClick={() => navigateMonth(-1)} className="p-2 hover:bg-gray-100 rounded-lg">
                            <ChevronLeft className="w-4 h-4" />
                        </button>
                        <h2 className="text-lg font-semibold text-gray-900 min-w-0 px-3">{formatDate(currentDate)}</h2>
                        <button onClick={() => navigateMonth(1)} className="p-2 hover:bg-gray-100 rounded-lg">
                            <ChevronRight className="w-4 h-4" />
                        </button>
                    </div>
                    <button onClick={goToToday} className="px-3 py-1 text-sm font-medium text-indigo-600 hover:bg-indigo-50 rounded-lg">วันนี้</button>
                </div>
            </div>
            
            <div className="flex items-center space-x-4 mb-4">
                <div className="flex items-center space-x-2">
                    <Filter className="w-4 h-4 text-gray-500" />
                    <select 
                        value={filters.unit} 
                        onChange={(e) => setFilters(prev => ({ ...prev, unit: e.target.value }))} 
                        className="border border-gray-300 rounded-lg px-3 py-2 text-sm"
                    >
                        <option value="">ทุกส่วนงาน</option>
                        {units.map(unit => (
                            <option key={`unit-${unit.id}`} value={unit.id}>{unit.unit_name}</option>
                        ))}
                    </select>
                </div>
                <div className="flex items-center space-x-2">
                    <Search className="w-4 h-4 text-gray-500" />
                    <input 
                        type="text" 
                        placeholder="ค้นหาเจ้าหน้าที่..." 
                        value={filters.officer} 
                        onChange={(e) => setFilters(prev => ({ ...prev, officer: e.target.value }))} 
                        className="border border-gray-300 rounded-lg px-3 py-2 text-sm" 
                    />
                </div>
            </div>
            
            <div className="grid grid-cols-7 gap-1">
                {['อา', 'จ', 'อ', 'พ', 'พฤ', 'ศ', 'ส'].map((day, index) => (
                    <div key={`day-header-${index}`} className="p-3 text-center text-sm font-medium text-gray-600 border-b">
                        {day}
                    </div>
                ))}
                {getDaysInMonth(currentDate).map((date, index) => (
                    <div key={`calendar-day-${index}`} className="min-h-[100px] border border-gray-200 p-2">
                        {date && (
                            <>
                                <div className="text-sm font-medium text-gray-900 mb-2">{date.getDate()}</div>
                                <div className="space-y-1">
                                    {getSchedulesForDate(date).map((schedule) => (
                                        <div 
                                            key={`schedule-${schedule.id}`} 
                                            onClick={() => openEditModal(schedule)} 
                                            className={`text-xs p-1 rounded cursor-pointer hover:opacity-80 ${schedule.shift_category === 'In-hours' ? 'bg-blue-100 text-blue-800' : 'bg-indigo-100 text-indigo-800'}`}
                                        >
                                            <div className="font-medium truncate flex items-center">
                                                {getShiftIcon(getShiftTypeForSchedule(schedule), "w-3 h-3 mr-1")}
                                                {schedule.shift_type_name}
                                            </div>
                                            <div className="truncate">{schedule.officer_names}</div>
                                            {schedule.unit_name && <div className="truncate text-xs opacity-75">{schedule.unit_name}</div>}
                                        </div>
                                    ))}
                                </div>
                                {getSchedulesForDate(date).length === 0 && (
                                    <button 
                                        onClick={() => openAddModal(date)} 
                                        className="w-full h-8 text-xs text-gray-400 hover:text-gray-600 hover:bg-gray-50 rounded flex items-center justify-center"
                                    >
                                        + เพิ่มเวร
                                    </button>
                                )}
                            </>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

// --- Main Component ---
const ShiftWorkPage = () => {
    const [currentDate, setCurrentDate] = useState(new Date());
    const [schedules, setSchedules] = useState([]);
    const [officers, setOfficers] = useState([]);
    const [shiftTypes, setShiftTypes] = useState([]);
    const [units, setUnits] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filters, setFilters] = useState({ unit: '', officer: '' });

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalMode, setModalMode] = useState('add');
    const [selectedSchedule, setSelectedSchedule] = useState(null);
    const [formData, setFormData] = useState({ duty_date: '', shift_type_id: '', unit_id: '', officer_ids: [], notes: '' });
    const [officerSearch, setOfficerSearch] = useState('');
    const [saving, setSaving] = useState(false);

    useEffect(() => { loadInitialData(); }, []);
    useEffect(() => { if (!loading) loadSchedules(); }, [currentDate]);

    useEffect(() => {
        const selectedShiftType = shiftTypes.find(type => type.id == formData.shift_type_id);
        if (formData.unit_id && selectedShiftType?.shift_category === 'In-hours') {
            const officersInUnit = officers.filter(o => o.unit_id == formData.unit_id).map(o => o.id);
            setFormData(prev => ({ ...prev, officer_ids: officersInUnit }));
        }
    }, [formData.unit_id, formData.shift_type_id, officers, shiftTypes]);

    const loadInitialData = async () => { 
        setLoading(true);
        try {
            await Promise.all([loadOfficers(), loadShiftTypes(), loadUnits(), loadSchedules()]);
        } catch (error) { 
            console.error('Error loading initial data:', error); 
        } finally { 
            setLoading(false); 
        }
    };

    const loadSchedules = async () => { 
        try {
            const month = currentDate.getMonth() + 1;
            const year = currentDate.getFullYear();
            const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_schedules&month=${month}&year=${year}`);
            const data = await response.json();
            if (data.success) { setSchedules(data.data); }
        } catch (error) { 
            console.error('Error loading schedules:', error); 
        }
    };

    const loadOfficers = async () => { 
        try {
            const response = await fetch(`${CONFIG.OFFICERS_API}?action=get_all_officers`);
            const data = await response.json();
            if (data.success) { setOfficers(data.data); }
        } catch (error) { 
            console.error('Error loading officers:', error); 
        }
    };

    const loadShiftTypes = async () => { 
        try {
            const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_shifttypes`);
            const data = await response.json();
            setShiftTypes(data);
        } catch (error) { 
            console.error('Error loading shift types:', error); 
        }
    };

    const loadUnits = async () => { 
        try {
            const response = await fetch(`${CONFIG.SCHEDULES_API}?action=get_all_units`);
            const data = await response.json();
            if(Array.isArray(data)) { setUnits(data); }
        } catch (error) { 
            console.error('Error loading units:', error); 
        }
    };

    const navigateMonth = (direction) => setCurrentDate(prev => { 
        const d = new Date(prev); 
        d.setMonth(prev.getMonth() + direction); 
        return d; 
    });

    const goToToday = () => setCurrentDate(new Date());

    const openAddModal = (date) => {
        setModalMode('add');
        setSelectedSchedule(null);
        setFormData({ 
            duty_date: date ? date.toISOString().split('T')[0] : '', 
            shift_type_id: '', 
            unit_id: '', 
            officer_ids: [], 
            notes: '' 
        });
        setOfficerSearch('');
        setIsModalOpen(true);
    };

    const openEditModal = (schedule) => {
        setModalMode('edit');
        setSelectedSchedule(schedule);
        setFormData({ 
            duty_date: schedule.duty_date, 
            shift_type_id: schedule.shift_type_id, 
            unit_id: schedule.unit_id || '', 
            officer_ids: schedule.officer_ids || [], 
            notes: schedule.notes || '' 
        });
        setOfficerSearch('');
        setIsModalOpen(true);
    };

    const closeModal = () => setIsModalOpen(false);

    const handleInputChange = (field, value) => {
        console.log(`กำลังเปลี่ยนค่า ${field} เป็น:`, value);
        setFormData(prev => ({ ...prev, [field]: value }));
    };

    const handleOfficerSelect = (officerId) => setFormData(prev => ({ 
        ...prev, 
        officer_ids: prev.officer_ids.includes(officerId) 
            ? prev.officer_ids.filter(id => id !== officerId) 
            : [...prev.officer_ids, officerId] 
    }));

    const handleSave = async () => { 
        setSaving(true);
        try {
            const payload = { 
                action: modalMode === 'edit' ? 'update_schedule' : 'create_schedule', 
                ...formData, 
                ...(modalMode === 'edit' && { schedule_id: selectedSchedule.id }) 
            };
            const response = await fetch(CONFIG.SCHEDULES_API, { 
                method: 'POST', 
                headers: { 'Content-Type': 'application/json' }, 
                body: JSON.stringify(payload) 
            });
            const result = await response.json();
            if (!result.success) throw new Error(result.message);
            await loadSchedules();
            closeModal();
        } catch (error) { 
            console.error('Error saving schedule:', error); 
        } finally { 
            setSaving(false); 
        }
    };

    const handleDelete = async () => { 
        if (!selectedSchedule || !window.confirm('คุณแน่ใจหรือไม่ที่จะลบตารางเวรนี้?')) return;
        setSaving(true);
        try {
            const payload = { action: 'delete_schedule', schedule_id: selectedSchedule.id };
            const response = await fetch(CONFIG.SCHEDULES_API, { 
                method: 'POST', 
                headers: { 'Content-Type': 'application/json' }, 
                body: JSON.stringify(payload) 
            });
            const result = await response.json();
            if (!result.success) throw new Error(result.message);
            await loadSchedules();
            closeModal();
        } catch (error) { 
            console.error('Error deleting schedule:', error); 
        } finally { 
            setSaving(false); 
        }
    };
    
    const filteredSchedules = useMemo(() => schedules.filter(schedule => {
        const unitMatch = !filters.unit || schedule.unit_id == filters.unit;
        const officerMatch = !filters.officer || (schedule.officer_names && schedule.officer_names.toLowerCase().includes(filters.officer.toLowerCase()));
        return unitMatch && officerMatch;
    }), [schedules, filters]);

    const today = new Date().toISOString().split('T')[0];
    const isFormInvalid = !formData.duty_date || !formData.duty_date.trim() || !formData.shift_type_id || formData.officer_ids.length === 0;

    const selectedShiftType = useMemo(() => 
        shiftTypes.find(type => type.id == formData.shift_type_id), 
        [shiftTypes, formData.shift_type_id]
    );
    
    const filteredOfficers = officers.filter(officer => {
        if (officer.unit_id === null) return false;
        if (!officerSearch) return true;
        const fullName = `${officer.prefix || ''}${officer.firstname || ''}${officer.lastname || ''}`.toLowerCase();
        return fullName.includes(officerSearch.toLowerCase());
    });
    
    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-96">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900">งานและเวร</h1>
                    <p className="text-sm text-gray-600 mt-1">จัดการตารางเวรและเอกสารที่เกี่ยวข้อง</p>
                </div>
                <button 
                    onClick={() => openAddModal(new Date())} 
                    className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700"
                >
                    <Plus className="w-4 h-4 mr-2" />เพิ่มเวร
                </button>
            </div>

            <ShiftCalendarView 
                currentDate={currentDate} 
                schedules={filteredSchedules} 
                navigateMonth={navigateMonth} 
                goToToday={goToToday} 
                openAddModal={openAddModal} 
                openEditModal={openEditModal} 
                filters={filters} 
                setFilters={setFilters} 
                units={units} 
                shiftTypes={shiftTypes}
            />

            {isModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                    <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] flex flex-col">
                        <div className="p-6 border-b">
                            <h3 className="text-lg font-semibold text-gray-900">
                                {modalMode === 'edit' ? 'แก้ไขตารางเวร' : 'เพิ่มตารางเวรใหม่'}
                            </h3>
                        </div>
                        
                        <div className="p-6 space-y-4 overflow-y-auto">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    วันที่ <span className="text-red-500">*</span>
                                </label>
                                <input 
                                    type="date" 
                                    value={formData.duty_date} 
                                    min={today} 
                                    onChange={(e) => handleInputChange('duty_date', e.target.value)} 
                                    className="w-full border border-gray-300 rounded-lg px-3 py-2"
                                />
                            </div>
                            
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">
                                    ประเภทเวร <span className="text-red-500">*</span>
                                </label>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                                    {shiftTypes.map(type => (
                                        <label 
                                            key={`shift-type-${type.id}`} 
                                            className={`flex items-center p-3 border rounded-lg cursor-pointer transition-all ${getButtonColor(type, formData.shift_type_id == type.id)}`}
                                        >
                                            <input 
                                                type="radio" 
                                                name="shift_type_id" 
                                                value={type.id} 
                                                checked={formData.shift_type_id == type.id} 
                                                onChange={(e) => handleInputChange('shift_type_id', e.target.value)} 
                                                className="hidden"
                                            />
                                            {getShiftIcon(type)}
                                            <span className="text-sm font-medium">{type.type_name}</span>
                                        </label>
                                    ))}
                                </div>
                            </div>
                            
                            {formData.shift_type_id && (
                                <div className="p-4 bg-slate-50 rounded-lg space-y-4">
                                    {selectedShiftType?.shift_category === 'In-hours' && (
                                        <div>
                                            <label className="flex items-center text-sm font-medium text-gray-700 mb-2">
                                                <Building className="w-4 h-4 mr-2"/>
                                                ส่วนงาน <span className="text-red-500">*</span>
                                            </label>
                                            <select 
                                                value={formData.unit_id} 
                                                onChange={(e) => handleInputChange('unit_id', e.target.value)} 
                                                className="w-full border border-gray-300 rounded-lg px-3 py-2"
                                            >
                                                <option value="">เลือกส่วนงาน</option>
                                                {units.map(unit => (
                                                    <option key={`modal-unit-${unit.id}`} value={unit.id}>
                                                        {unit.unit_name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                    )}
                                    
                                    <div className="relative">
                                        <Search className="absolute left-3 top-3 w-4 h-4 text-gray-400"/>
                                        <input 
                                            type="text" 
                                            placeholder="ค้นหารายชื่อเจ้าหน้าที่..." 
                                            value={officerSearch} 
                                            onChange={e => setOfficerSearch(e.target.value)} 
                                            className="w-full border border-gray-300 rounded-lg px-3 py-2 pl-9 text-sm"
                                        />
                                    </div>
                                    <div className="border border-gray-300 rounded-lg max-h-48 overflow-y-auto">
                                        {filteredOfficers.length > 0 ? filteredOfficers.map(officer => (
                                            <label 
                                                key={`officer-${officer.id}`} 
                                                className="flex items-center py-2 px-3 hover:bg-gray-100 cursor-pointer border-b last:border-b-0"
                                            >
                                                <input 
                                                    type="checkbox" 
                                                    checked={formData.officer_ids.includes(officer.id)} 
                                                    onChange={() => handleOfficerSelect(officer.id)} 
                                                    className="h-4 w-4 text-indigo-600"
                                                />
                                                <span className="ml-3 text-sm text-gray-900">
                                                    {officer.prefix} {officer.firstname} {officer.lastname}
                                                </span>
                                            </label>
                                        )) : (
                                            <p className="p-4 text-sm text-center text-gray-500">ไม่พบรายชื่อ</p>
                                        )}
                                    </div>
                                </div>
                            )}

                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-2">หมายเหตุ</label>
                                <textarea 
                                    value={formData.notes} 
                                    onChange={(e) => handleInputChange('notes', e.target.value)} 
                                    rows={3} 
                                    className="w-full border border-gray-300 rounded-lg px-3 py-2" 
                                    placeholder="รายละเอียดเพิ่มเติม..."
                                />
                            </div>
                        </div>

                        <div className="p-6 border-t bg-gray-50 flex justify-between items-center">
                            <div>
                                {modalMode === 'edit' && (
                                    <button 
                                        onClick={handleDelete} 
                                        disabled={saving} 
                                        className="text-sm text-red-600 hover:text-red-800"
                                    >
                                        ลบเวรนี้
                                    </button>
                                )}
                            </div>
                            <div className="flex space-x-3">
                                <button 
                                    onClick={closeModal} 
                                    disabled={saving} 
                                    className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50"
                                >
                                    ยกเลิก
                                </button>
                                <button 
                                    onClick={handleSave} 
                                    disabled={isFormInvalid || saving} 
                                    className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
                                >
                                    {saving ? (
                                        <>
                                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                                            กำลังบันทึก...
                                        </>
                                    ) : (
                                        <>
                                            <Save className="w-4 h-4 mr-2" />
                                            บันทึก
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ShiftWorkPage;
