import React, { useState, useMemo } from 'react';
import { 
  ChevronLeft, 
  Users, 
  Search, 
  UserPlus, 
  Save,
  User,
  Crown,
  Star,
  Plus,
  Trash2
} from 'lucide-react';
import { CONFIG } from '../../config';
import CurrentMemberCard from './CurrentMemberCard';
import AvailableOfficerCard from './AvailableOfficerCard';

// Component สำหรับจัดการบุคลากรในส่วนงาน
const UnitManagementView = ({ 
  selectedUnit, 
  units, 
  officers, 
  unitStructures, 
  searchTerm, 
  setSearchTerm, 
  onBack, 
  onRefresh 
}) => {
  const [selectedOfficers, setSelectedOfficers] = useState([]);
  const [isAddingMultiple, setIsAddingMultiple] = useState(false);

  // กรองเจ้าหน้าที่ในส่วนงานที่เลือก
  const currentUnitMembers = useMemo(() => {
    return unitStructures
      .filter(item => item.unit_code === selectedUnit.unit_code)
      .sort((a, b) => {
        // เรียงลำดับ: director > supervisor > member
        if (a.role === 'director') return -1;
        if (b.role === 'director') return 1;
        if (a.role === 'supervisor') return -1;
        if (b.role === 'supervisor') return 1;
        return a.seniority_order - b.seniority_order;
      });
  }, [unitStructures, selectedUnit]);

  // กรองเจ้าหน้าที่ที่ยังไม่ได้อยู่ในโครงสร้าง
  const availableOfficers = useMemo(() => {
    const usedPositions = unitStructures.map(s => s.position_number);
    return officers.filter(officer => !usedPositions.includes(officer.position_number));
  }, [officers, unitStructures]);

  // กรองเจ้าหน้าที่ตามการค้นหา
  const filteredAvailableOfficers = useMemo(() => {
    if (!searchTerm) return availableOfficers;
    
    return availableOfficers.filter(officer => {
      const fullName = `${officer.prefix} ${officer.firstname} ${officer.lastname}`.toLowerCase();
      const positionNumber = officer.position_number.toString().toLowerCase();
      return fullName.includes(searchTerm.toLowerCase()) || 
             positionNumber.includes(searchTerm.toLowerCase());
    });
  }, [availableOfficers, searchTerm]);

  // ฟังก์ชันสำหรับเพิ่มหลายคนพร้อมกัน
  const handleBulkAdd = async () => {
    if (selectedOfficers.length === 0) return;

    try {
      const promises = selectedOfficers.map(async (positionNumber) => {
        const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            position_number: positionNumber,
            unit_code: selectedUnit.unit_code,
            role: selectedUnit.isDirector ? 'director' : 'member',
            seniority_order: currentUnitMembers.length + 1
          })
        });
        return response.json();
      });

      const results = await Promise.all(promises);
      const failures = results.filter(r => !r.success);
      
      if (failures.length > 0) {
        alert(`เพิ่มบุคลากรไม่สำเร็จ ${failures.length} คน`);
      } else {
        alert(`เพิ่มบุคลากรสำเร็จ ${selectedOfficers.length} คน`);
      }

      setSelectedOfficers([]);
      setIsAddingMultiple(false);
      onRefresh();
    } catch (error) {
      console.error('Error adding multiple officers:', error);
      alert('เกิดข้อผิดพลาดในการเพิ่มบุคลากร');
    }
  };

  return (
    <div className="p-6 bg-slate-50 rounded-2xl">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-4">
          <button
            onClick={onBack}
            className="flex items-center px-3 py-2 text-slate-600 hover:text-slate-800 transition-colors"
          >
            <ChevronLeft className="w-5 h-5 mr-1" />
            กลับ
          </button>
          <div>
            <h2 className="text-2xl font-bold text-slate-800">{selectedUnit.unit_name}</h2>
            <p className="text-slate-500">จัดการบุคลากรในส่วนงาน</p>
          </div>
        </div>
        <div className="flex space-x-3">
          <button
            onClick={() => {
              if (isAddingMultiple) {
                handleBulkAdd();
              } else {
                setIsAddingMultiple(true);
              }
            }}
            className={`flex items-center px-4 py-2 rounded-lg transition-colors ${
              isAddingMultiple 
                ? 'bg-green-600 text-white hover:bg-green-700' 
                : 'bg-indigo-600 text-white hover:bg-indigo-700'
            }`}
          >
            {isAddingMultiple ? (
              <>
                <Save className="w-4 h-4 mr-2" />
                บันทึก ({selectedOfficers.length})
              </>
            ) : (
              <>
                <UserPlus className="w-4 h-4 mr-2" />
                เพิ่มหลายคน
              </>
            )}
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Current Members */}
        <div className="bg-white rounded-xl p-6">
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <Users className="w-5 h-5 mr-2 text-indigo-600" />
            บุคลากรปัจจุบัน ({currentUnitMembers.length} คน)
          </h3>
          
          <div className="space-y-3 max-h-96 overflow-y-auto">
            {currentUnitMembers.length === 0 ? (
              <div className="text-center py-8 text-slate-500">
                <Users className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p>ยังไม่มีบุคลากรในส่วนงานนี้</p>
              </div>
            ) : (
              currentUnitMembers.map(member => (
                <CurrentMemberCard 
                  key={member.id} 
                  member={member} 
                  onRefresh={onRefresh}
                />
              ))
            )}
          </div>
        </div>

        {/* Available Officers */}
        <div className="bg-white rounded-xl p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold flex items-center">
              <Search className="w-5 h-5 mr-2 text-green-600" />
              เพิ่มบุคลากร
            </h3>
            {isAddingMultiple && (
              <span className="text-sm text-slate-500">
                เลือกแล้ว {selectedOfficers.length} คน
              </span>
            )}
          </div>

          {/* Search Box */}
          <div className="relative mb-4">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
            <input
              type="text"
              placeholder="ค้นหาชื่อ หรือเลขตำแหน่ง..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-9 pr-3 py-2 border border-slate-200 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
          </div>

          <div className="space-y-2 max-h-96 overflow-y-auto">
            {filteredAvailableOfficers.length === 0 ? (
              <div className="text-center py-8 text-slate-500">
                <Search className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p>ไม่พบเจ้าหน้าที่ที่ตรงกับการค้นหา</p>
              </div>
            ) : (
              filteredAvailableOfficers.map(officer => (
                <AvailableOfficerCard
                  key={officer.position_number}
                  officer={officer}
                  selectedUnit={selectedUnit}
                  isMultipleMode={isAddingMultiple}
                  isSelected={selectedOfficers.includes(officer.position_number)}
                  onToggleSelect={(positionNumber) => {
                    if (selectedOfficers.includes(positionNumber)) {
                      setSelectedOfficers(prev => prev.filter(p => p !== positionNumber));
                    } else {
                      setSelectedOfficers(prev => [...prev, positionNumber]);
                    }
                  }}
                  onAddSingle={onRefresh}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

