                    ))}
                  </div>
                </div>
              </div>

              {/* à¸£à¸²à¸¢à¸Šà¸·à¹ˆà¸­à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¸ˆà¸°à¹€à¸‚à¹‰à¸²à¹€à¸§à¸£ */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  à¸£à¸²à¸¢à¸Šà¸·à¹ˆà¸­à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¸ˆà¸°à¹€à¸‚à¹‰à¸²à¹€à¸§à¸£
                  {availableOfficers.length > 0 && (
                    <span className="text-sm text-blue-600 ml-2">({availableOfficers.length} à¸„à¸™)</span>
                  )}
                </label>
                <div className="border border-gray-300 rounded-lg p-3 max-h-60 overflow-y-auto">
                  {/* Manual search / add single officer */}
                  <div className="mb-3 flex items-center space-x-2">
                    <input
                      type="text"
                      placeholder="à¸„à¹‰à¸™à¸«à¸²/à¹€à¸žà¸´à¹ˆà¸¡à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ (à¸£à¸°à¸šà¸¸à¸«à¸¡à¸²à¸¢à¹€à¸¥à¸‚à¸•à¸³à¹à¸«à¸™à¹ˆà¸‡à¸«à¸£à¸·à¸­à¸Šà¸·à¹ˆà¸­)"
                      value={manualSearch}
                      onChange={(e) => setManualSearch(e.target.value)}
                      className="flex-1 border border-gray-300 rounded-lg px-3 py-2 text-sm"
                    />
                    <button
                      type="button"
                      onClick={() => {
                        const q = manualSearch && manualSearch.trim();
                        if (!q) return showAlert('à¸à¸£à¸¸à¸“à¸²à¸à¸£à¸­à¸à¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¹€à¸žà¸·à¹ˆà¸­à¸„à¹‰à¸™à¸«à¸²/à¹€à¸žà¸´à¹ˆà¸¡', 'error');
                        // à¸«à¸²à¹ƒà¸™ officers à¸ˆà¸²à¸ unit_structure
                        const found = officers.find(o => String(o.position_number) === q || (`${o.firstname} ${o.lastname}`).toLowerCase().includes(q.toLowerCase()));
                        if (found) {
                          // à¸–à¹‰à¸²à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸¡à¸µà¹ƒà¸™ manualAdded à¸«à¸£à¸·à¸­ available à¹ƒà¸«à¹‰à¹€à¸žà¸´à¹ˆà¸¡
                          const key = String(found.position_number);
                          const already = manualAddedOfficers.find(a => String(a.position_number) === key) || availableOfficers.find(a => String(a.position_number) === key);
                          if (already) {
                            showAlert('à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸„à¸™à¸™à¸µà¹‰à¸–à¸¹à¸à¹€à¸žà¸´à¹ˆà¸¡à¹à¸¥à¹‰à¸§', 'info');
                          } else {
                            setManualAddedOfficers(prev => [...prev, found]);
                            showAlert('à¹€à¸žà¸´à¹ˆà¸¡à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ', 'success');
                          }
                        } else {
                          showAlert('à¹„à¸¡à¹ˆà¸žà¸šà¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸•à¸²à¸¡à¸„à¸³à¸„à¹‰à¸™', 'error');
                        }
                        setManualSearch('');
                      }}
                      className="px-3 py-2 bg-indigo-600 text-white rounded-lg text-sm"
                    >à¹€à¸žà¸´à¹ˆà¸¡</button>
                  </div>

                  {/* Combined list: available + manualAdded (filter removed) */}
                  {((availableOfficers && availableOfficers.length > 0) || (manualAddedOfficers && manualAddedOfficers.length > 0)) ? (
                    <div className="space-y-2">
                      {availableOfficers.map(officer => {
                        const removed = removedPositions.includes(String(officer.position_number));
                        return (
                          <div key={officer.position_number} className={`flex items-center justify-between ${removed ? 'opacity-50 line-through' : ''} bg-green-50 border border-green-200 px-3 py-2 rounded-lg`}>
                            <div className="flex items-center">
                              <CheckCircle className="w-4 h-4 text-green-600 mr-2" />
                              <div>
                                <div className="text-sm font-medium text-green-900">{officer.prefix} {officer.firstname} {officer.lastname}</div>
                                <div className="text-xs text-green-700">({officer.unit_name})</div>
                              </div>
                            </div>
                            <div className="flex items-center space-x-2">
                              <button
                                type="button"
                                onClick={() => {
                                  const key = String(officer.position_number);
                                  setRemovedPositions(prev => prev.includes(key) ? prev.filter(p => p !== key) : [...prev, key]);
                                }}
                                className={`px-2 py-1 text-xs rounded ${removed ? 'bg-yellow-50 text-yellow-800 border border-yellow-200' : 'bg-white text-red-600 border border-red-200'}`}
                              >{removed ? 'à¸¢à¸à¹€à¸¥à¸´à¸à¸à¸²à¸£à¸¥à¸š' : 'à¸¥à¸š'}</button>
                            </div>
                          </div>
                        );
                      })}

                      {manualAddedOfficers.map(officer => (
                        <div key={`manual-${officer.position_number}`} className="flex items-center justify-between bg-blue-50 border border-blue-200 px-3 py-2 rounded-lg">
                          <div className="flex items-center">
                            <CheckCircle className="w-4 h-4 text-blue-600 mr-2" />
                            <div>
                              <div className="text-sm font-medium text-blue-900">{officer.prefix} {officer.firstname} {officer.lastname}</div>
                              <div className="text-xs text-blue-700">({officer.unit_name})</div>
                            </div>
                          </div>
                          <div>
                            <button
                              type="button"
                              onClick={() => setManualAddedOfficers(prev => prev.filter(a => String(a.position_number) !== String(officer.position_number)))}
