import React, { useState, useEffect, useMemo } from 'react';
import {
  Plus,
  Edit,
  Trash2,
  Save,
  X,
  Search,
  Users,
  Crown,
  Shield,
  User,
  Building,
  ChevronDown,
  ChevronRight,
  AlertTriangle,
  Check
} from 'lucide-react';
import { CONFIG } from '../config';

const UnitStructureManagementPage = () => {
  const [unitStructures, setUnitStructures] = useState([]);
  const [availableOfficers, setAvailableOfficers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('');
  const [filterUnit, setFilterUnit] = useState('');
  const [expandedUnits, setExpandedUnits] = useState(new Set());
  const [viewMode, setViewMode] = useState('hierarchy'); // 'hierarchy' หรือ 'list'
  
  const [formData, setFormData] = useState({
    position_number: '',
    unit_name: '',
    unit_number: '',
    role: 'member',
    seniority_order: 1
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadUnitStructures(),
        loadAvailableOfficers()
      ]);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadUnitStructures = async () => {
    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}?action=get_all_unit_structures`);
      const data = await response.json();
      if (data.success) {
        setUnitStructures(data.data);
      }
    } catch (error) {
      console.error('Error loading unit structures:', error);
    }
  };

  const loadAvailableOfficers = async () => {
    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}?action=get_available_officers`);
      const data = await response.json();
      if (data.success) {
        setAvailableOfficers(data.data);
      }
    } catch (error) {
      console.error('Error loading available officers:', error);
    }
  };

  const openModal = (item = null) => {
    if (item) {
      setEditingItem(item);
      setFormData({
        position_number: item.position_number,
        unit_name: item.unit_name,
        unit_number: item.unit_number,
        role: item.role,
        seniority_order: item.seniority_order
      });
    } else {
      setEditingItem(null);
      setFormData({
        position_number: '',
        unit_name: '',
        unit_number: '',
        role: 'member',
        seniority_order: 1
      });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingItem(null);
    setFormData({
      position_number: '',
      unit_name: '',
      unit_number: '',
      role: 'member',
      seniority_order: 1
    });
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const action = editingItem ? 'update_unit_structure' : 'create_unit_structure';
      const payload = editingItem 
        ? { action, id: editingItem.id, ...formData }
        : { action, ...formData };

      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const result = await response.json();
      if (result.success) {
        await loadData();
        closeModal();
        alert(editingItem ? 'อัปเดตข้อมูลสำเร็จ' : 'เพิ่มข้อมูลสำเร็จ');
      } else {
        alert('เกิดข้อผิดพลาด: ' + result.message);
      }
    } catch (error) {
      console.error('Error saving:', error);
      alert('เกิดข้อผิดพลาดในการบันทึกข้อมูล');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (item) => {
    if (!window.confirm(`คุณแน่ใจหรือไม่ที่จะลบ ${item.full_name} ออกจากโครงสร้างส่วนงาน?`)) {
      return;
    }

    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'delete_unit_structure',
          id: item.id
        })
      });

      const result = await response.json();
      if (result.success) {
        await loadData();
        alert('ลบข้อมูลสำเร็จ');
      } else {
        alert('เกิดข้อผิดพลาด: ' + result.message);
      }
    } catch (error) {
      console.error('Error deleting:', error);
      alert('เกิดข้อผิดพลาดในการลบข้อมูล');
    }
  };

  const toggleUnitExpansion = (unitNumber) => {
    setExpandedUnits(prev => {
      const newSet = new Set(prev);
      if (newSet.has(unitNumber)) {
        newSet.delete(unitNumber);
      } else {
        newSet.add(unitNumber);
      }
      return newSet;
    });
  };

  // จัดกลุ่มข้อมูลตามหน่วยงาน
  const groupedData = useMemo(() => {
    const director = unitStructures.find(item => item.role === 'director');
    const units = {};

    unitStructures.forEach(item => {
      if (item.role !== 'director') {
        if (!units[item.unit_number]) {
          units[item.unit_number] = {
            unit_number: item.unit_number,
            unit_name: item.unit_name,
            supervisor: null,
            members: []
          };
        }

        if (item.role === 'supervisor') {
          units[item.unit_number].supervisor = item;
        } else {
          units[item.unit_number].members.push(item);
        }
      }
    });

    // เรียงลำดับสมาชิกตาม seniority_order
    Object.values(units).forEach(unit => {
      unit.members.sort((a, b) => a.seniority_order - b.seniority_order);
    });

    return { director, units: Object.values(units) };
  }, [unitStructures]);

  // กรองข้อมูลสำหรับ List View
  const filteredData = useMemo(() => {
    return unitStructures.filter(item => {
      const matchesSearch = !searchTerm || 
        item.full_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.unit_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.unit_number?.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesRole = !filterRole || item.role === filterRole;
      const matchesUnit = !filterUnit || item.unit_number === filterUnit;
      
      return matchesSearch && matchesRole && matchesUnit;
    });
  }, [unitStructures, searchTerm, filterRole, filterUnit]);

  const getRoleIcon = (role) => {
    switch (role) {
      case 'director': return <Crown className="w-4 h-4 text-purple-600" />;
      case 'supervisor': return <Shield className="w-4 h-4 text-blue-600" />;
      case 'member': return <User className="w-4 h-4 text-green-600" />;
      default: return <User className="w-4 h-4 text-gray-600" />;
    }
  };

  const getRoleLabel = (role) => {
    switch (role) {
      case 'director': return 'ผู้อำนวยการศูนย์';
      case 'supervisor': return 'หัวหน้าส่วน';
      case 'member': return 'เจ้าหน้าที่';
      default: return role;
    }
  };

  const getRoleBadgeClass = (role) => {
    switch (role) {
      case 'director': return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'supervisor': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'member': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <div className="flex-grow p-6 bg-slate-50/50 overflow-y-auto">
        <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-6">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <div>
              <h1 className="text-2xl font-bold text-slate-800">จัดการโครงสร้างส่วนงาน</h1>
              <p className="text-slate-500 mt-1">จัดการโครงสร้างบุคลากรและลำดับขั้นในองค์กร</p>
            </div>
            <div className="flex items-center space-x-3">
              {/* View Mode Toggle */}
              <div className="flex bg-slate-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('hierarchy')}
                  className={`px-3 py-1 text-sm rounded-md transition-colors ${
                    viewMode === 'hierarchy'
                      ? 'bg-white text-slate-800 shadow-sm'
                      : 'text-slate-600 hover:text-slate-800'
                  }`}
                >
                  โครงสร้าง
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`px-3 py-1 text-sm rounded-md transition-colors ${
                    viewMode === 'list'
                      ? 'bg-white text-slate-800 shadow-sm'
                      : 'text-slate-600 hover:text-slate-800'
                  }`}
                >
                  รายการ
                </button>
              </div>
              
              <button
                onClick={() => openModal()}
                className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700"
              >
                <Plus className="w-4 h-4 mr-2" />
                เพิ่มบุคลากร
              </button>
            </div>
          </div>

          {/* Filters (แสดงเฉพาะใน List View) */}
          {viewMode === 'list' && (
            <div className="flex flex-wrap items-center gap-4 mb-6 p-4 bg-slate-50 rounded-lg">
              <div className="flex-1 min-w-64">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
                  <input
                    type="text"
                    placeholder="ค้นหาชื่อ, ส่วนงาน, หมายเลขส่วนงาน..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-9 pr-3 py-2 border border-slate-200 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  />
                </div>
              </div>
              
              <select
                value={filterRole}
                onChange={(e) => setFilterRole(e.target.value)}
                className="px-3 py-2 border border-slate-200 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              >
                <option value="">ทุกตำแหน่ง</option>
                <option value="director">ผู้อำนวยการศูนย์</option>
                <option value="supervisor">หัวหน้าส่วน</option>
                <option value="member">เจ้าหน้าที่</option>
              </select>
              
              <select
                value={filterUnit}
                onChange={(e) => setFilterUnit(e.target.value)}
                className="px-3 py-2 border border-slate-200 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              >
                <option value="">ทุกส่วนงาน</option>
                {[...new Set(unitStructures.map(item => item.unit_number))].filter(Boolean).map(unitNumber => (
                  <option key={unitNumber} value={unitNumber}>
                    {unitNumber.toUpperCase()}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Content */}
          {viewMode === 'hierarchy' ? (
            <HierarchyView 
              groupedData={groupedData}
              expandedUnits={expandedUnits}
              toggleUnitExpansion={toggleUnitExpansion}
              onEdit={openModal}
              onDelete={handleDelete}
              getRoleIcon={getRoleIcon}
              getRoleLabel={getRoleLabel}
              getRoleBadgeClass={getRoleBadgeClass}
            />
          ) : (
            <ListView 
              data={filteredData}
              onEdit={openModal}
              onDelete={handleDelete}
              getRoleIcon={getRoleIcon}
              getRoleLabel={getRoleLabel}
              getRoleBadgeClass={getRoleBadgeClass}
            />
          )}
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <UnitStructureModal
          isOpen={showModal}
          editingItem={editingItem}
          formData={formData}
          setFormData={setFormData}
          availableOfficers={availableOfficers}
          onSave={handleSave}
          onClose={closeModal}
          saving={saving}
        />
      )}
    </div>
  );
};

// Hierarchy View Component
const HierarchyView = ({ groupedData, expandedUnits, toggleUnitExpansion, onEdit, onDelete, getRoleIcon, getRoleLabel, getRoleBadgeClass }) => {
  return (
    <div className="space-y-6">
      {/* Director */}
      {groupedData.director && (
        <div className="border-2 border-purple-200 rounded-lg p-4 bg-purple-50">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                <Crown className="w-6 h-6 text-purple-600" />
              </div>
              <div>
                <h3 className="font-semibold text-slate-800">{groupedData.director.full_name || 'ไม่ระบุชื่อ'}</h3>
                <p className="text-sm text-slate-600">ผู้อำนวยการศูนย์รักษาความปลอดภัย</p>
                <p className="text-xs text-slate-500">เลขตำแหน่ง: {groupedData.director.position_number}</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <button
                onClick={() => onEdit(groupedData.director)}
                className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
              >
                <Edit className="w-4 h-4" />
              </button>
              <button
                onClick={() => onDelete(groupedData.director)}
                className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Units */}
      <div className="space-y-4">
        {groupedData.units.map(unit => (
          <div key={unit.unit_number} className="border border-slate-200 rounded-lg overflow-hidden">
            {/* Unit Header */}
            <div 
              className="p-4 bg-slate-50 cursor-pointer hover:bg-slate-100 transition-colors"
              onClick={() => toggleUnitExpansion(unit.unit_number)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  {expandedUnits.has(unit.unit_number) ? 
                    <ChevronDown className="w-5 h-5 text-slate-500" /> : 
                    <ChevronRight className="w-5 h-5 text-slate-500" />
                  }
                  <Building className="w-5 h-5 text-slate-600" />
                  <div>
                    <h3 className="font-semibold text-slate-800">{unit.unit_name}</h3>
                    <p className="text-sm text-slate-600">หมายเลขส่วนงาน: {unit.unit_number.toUpperCase()}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="px-2 py-1 bg-slate-200 text-slate-700 text-xs rounded-full">
                    {(unit.supervisor ? 1 : 0) + unit.members.length} คน
                  </span>
                </div>
              </div>
            </div>

            {/* Unit Members */}
            {expandedUnits.has(unit.unit_number) && (
              <div className="p-4 bg-white space-y-3">
                {/* Supervisor */}
                {unit.supervisor && (
                  <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                          <Shield className="w-5 h-5 text-blue-600" />
                        </div>
                        <div>
                          <h4 className="font-medium text-slate-800">{unit.supervisor.full_name || 'ไม่ระบุชื่อ'}</h4>
                          <p className="text-sm text-slate-600">หัวหน้าส่วน</p>
                          <p className="text-xs text-slate-500">เลขตำแหน่ง: {unit.supervisor.position_number}</p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full border border-blue-200">
                          ลำดับ {unit.supervisor.seniority_order}
                        </span>
                        <button
                          onClick={() => onEdit(unit.supervisor)}
                          className="p-1 text-blue-600 hover:bg-blue-100 rounded"
                        >
                          <Edit className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => onDelete(unit.supervisor)}
                          className="p-1 text-red-600 hover:bg-red-100 rounded"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                )}

                {/* Members */}
                {unit.members.map(member => (
                  <div key={member.id} className="p-3 bg-green-50 border border-green-200 rounded-lg">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                          <User className="w-5 h-5 text-green-600" />
                        </div>
                        <div>
                          <h4 className="font-medium text-slate-800">{member.full_name || 'ไม่ระบุชื่อ'}</h4>
                          <p className="text-sm text-slate-600">เจ้าหน้าที่</p>
                          <p className="text-xs text-slate-500">เลขตำแหน่ง: {member.position_number}</p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full border border-green-200">
                          ลำดับ {member.seniority_order}
                        </span>
                        <button
                          onClick={() => onEdit(member)}
                          className="p-1 text-blue-600 hover:bg-blue-100 rounded"
                        >
                          <Edit className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => onDelete(member)}
                          className="p-1 text-red-600 hover:bg-red-100 rounded"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}

                {!unit.supervisor && unit.members.length === 0 && (
                  <div className="text-center py-8 text-slate-500">
                    <Users className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p>ยังไม่มีบุคลากรในส่วนงานนี้</p>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      {groupedData.units.length === 0 && !groupedData.director && (
        <div className="text-center py-12 text-slate-500">
          <Building className="w-12 h-12 mx-auto mb-4 opacity-50" />
          <h3 className="text-lg font-medium mb-2">ยังไม่มีโครงสร้างส่วนงาน</h3>
          <p>เริ่มต้นสร้างโครงสร้างองค์กรของคุณ</p>
        </div>
      )}
    </div>
  );
};

// List View Component
const ListView = ({ data, onEdit, onDelete, getRoleIcon, getRoleLabel, getRoleBadgeClass }) => {
  return (
    <div className="space-y-3">
      {data.map(item => (
        <div key={item.id} className="p-4 border border-slate-200 rounded-lg hover:shadow-md transition-shadow">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-slate-100 rounded-full flex items-center justify-center">
                {getRoleIcon(item.role)}
              </div>
              <div>
                <h3 className="font-semibold text-slate-800">{item.full_name || 'ไม่ระบุชื่อ'}</h3>
                <p className="text-sm text-slate-600">{item.unit_name} ({item.unit_number?.toUpperCase()})</p>
                <p className="text-xs text-slate-500">เลขตำแหน่ง: {item.position_number}</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <span className={`px-3 py-1 text-xs rounded-full border ${getRoleBadgeClass(item.role)}`}>
                {getRoleLabel(item.role)}
              </span>
              <span className="px-2 py-1 bg-slate-100 text-slate-700 text-xs rounded-full">
                ลำดับ {item.seniority_order}
              </span>
              <button
                onClick={() => onEdit(item)}
                className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg"
              >
                <Edit className="w-4 h-4" />
              </button>
              <button
                onClick={() => onDelete(item)}
                className="p-2 text-red-600 hover:bg-red-50 rounded-lg"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      ))}

      {data.length === 0 && (
        <div className="text-center py-12 text-slate-500">
          <Search className="w-12 h-12 mx-auto mb-4 opacity-50" />
          <h3 className="text-lg font-medium mb-2">ไม่พบข้อมูลที่ค้นหา</h3>
          <p>ลองปรับเปลี่ยนคำค้นหาหรือตัวกรอง</p>
        </div>
      )}
    </div>
  );
};

// Modal Component
const UnitStructureModal = ({ isOpen, editingItem, formData, setFormData, availableOfficers, onSave, onClose, saving }) => {
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.position_number) {
      newErrors.position_number = 'กรุณาเลือกเจ้าหน้าที่';
    }
    
    if (!formData.unit_name.trim()) {
      newErrors.unit_name = 'กรุณาระบุชื่อส่วนงาน';
    }
    
    if (!formData.unit_number.trim()) {
      newErrors.unit_number = 'กรุณาระบุหมายเลขส่วนงาน';
    } else if (!/^[a-zA-Z]{2}\d{2}$/.test(formData.unit_number)) {
      newErrors.unit_number = 'รูปแบบต้องเป็น 2 ตัวอักษร + 2 ตัวเลข (เช่น cs01)';
    }
    
    if (!Number.isInteger(Number(formData.seniority_order)) || Number(formData.seniority_order) < 1) {
      newErrors.seniority_order = 'ลำดับอาวุโสต้องเป็นตัวเลขที่มากกว่า 0';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = () => {
    if (validateForm()) {
      onSave();
    }
  };

  const selectedOfficer = availableOfficers.find(officer => officer.position_number === formData.position_number);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl mx-4 max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b">
          <h3 className="text-lg font-semibold text-gray-900">
            {editingItem ? 'แก้ไขโครงสร้างส่วนงาน' : 'เพิ่มบุคลากรใหม่'}
          </h3>
        </div>

        <div className="p-6 space-y-6">
          {/* เลือกเจ้าหน้าที่ */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              เจ้าหน้าที่ <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.position_number}
              onChange={(e) => setFormData(prev => ({ ...prev, position_number: e.target.value }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.position_number ? 'border-red-300' : 'border-gray-300'
              }`}
              disabled={editingItem} // ไม่ให้แก้ไขเจ้าหน้าที่ถ้าเป็นการแก้ไข
            >
              <option value="">เลือกเจ้าหน้าที่</option>
              {editingItem ? (
                <option value={editingItem.position_number}>
                  {editingItem.full_name} (เลขตำแหน่ง: {editingItem.position_number})
                </option>
              ) : (
                availableOfficers.map(officer => (
                  <option key={officer.position_number} value={officer.position_number}>
                    {officer.full_name} (เลขตำแหน่ง: {officer.position_number})
                  </option>
                ))
              )}
            </select>
            {errors.position_number && (
              <p className="mt-1 text-sm text-red-600">{errors.position_number}</p>
            )}
            {selectedOfficer && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Check className="w-4 h-4 text-blue-600" />
                  <span className="text-sm text-blue-800">
                    เลือก: {selectedOfficer.full_name} (ตำแหน่ง: {selectedOfficer.rank})
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* ชื่อส่วนงาน */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ชื่อส่วนงาน <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formData.unit_name}
              onChange={(e) => setFormData(prev => ({ ...prev, unit_name: e.target.value }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.unit_name ? 'border-red-300' : 'border-gray-300'
              }`}
              placeholder="เช่น ส่วนรักษาความปลอดภัย"
            />
            {errors.unit_name && (
              <p className="mt-1 text-sm text-red-600">{errors.unit_name}</p>
            )}
          </div>

          {/* หมายเลขส่วนงาน */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              หมายเลขส่วนงาน <span className="text-red-500">*</span>
            </label>
            <input
              type="text"
              value={formData.unit_number}
              onChange={(e) => setFormData(prev => ({ ...prev, unit_number: e.target.value.toLowerCase() }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.unit_number ? 'border-red-300' : 'border-gray-300'
              }`}
              placeholder="เช่น cs01"
              maxLength={4}
            />
            {errors.unit_number && (
              <p className="mt-1 text-sm text-red-600">{errors.unit_number}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              รูปแบบ: 2 ตัวอักษร + 2 ตัวเลข (เช่น cs01, ad02)
            </p>
          </div>

          {/* บทบาท */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              บทบาท <span className="text-red-500">*</span>
            </label>
            <select
              value={formData.role}
              onChange={(e) => {
                const newRole = e.target.value;
                setFormData(prev => ({ 
                  ...prev, 
                  role: newRole,
                  seniority_order: newRole === 'supervisor' ? 1 : prev.seniority_order
                }));
              }}
              className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              <option value="member">เจ้าหน้าที่</option>
              <option value="supervisor">หัวหน้าส่วน</option>
              <option value="director">ผู้อำนวยการศูนย์</option>
            </select>
            
            {formData.role === 'director' && (
              <div className="mt-2 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                <div className="flex items-start space-x-2">
                  <AlertTriangle className="w-4 h-4 text-yellow-600 mt-0.5" />
                  <span className="text-sm text-yellow-800">
                    ผู้อำนวยการศูนย์สามารถมีได้เพียงคนเดียวในระบบ
                  </span>
                </div>
              </div>
            )}
            
            {formData.role === 'supervisor' && (
              <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                <div className="flex items-start space-x-2">
                  <AlertTriangle className="w-4 h-4 text-blue-600 mt-0.5" />
                  <span className="text-sm text-blue-800">
                    หัวหน้าส่วนจะมีลำดับอาวุโสเป็น 1 อัตโนมัติ และแต่ละส่วนงานสามารถมีได้เพียงคนเดียว
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* ลำดับอาวุโส */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              ลำดับอาวุโส <span className="text-red-500">*</span>
            </label>
            <input
              type="number"
              min="1"
              value={formData.seniority_order}
              onChange={(e) => setFormData(prev => ({ ...prev, seniority_order: parseInt(e.target.value) || 1 }))}
              className={`w-full border rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent ${
                errors.seniority_order ? 'border-red-300' : 'border-gray-300'
              }`}
              disabled={formData.role === 'supervisor'}
            />
            {errors.seniority_order && (
              <p className="mt-1 text-sm text-red-600">{errors.seniority_order}</p>
            )}
            <p className="mt-1 text-xs text-gray-500">
              หัวหน้าส่วนจะมีลำดับ 1 เสมอ สำหรับเจ้าหน้าที่ให้กำหนดลำดับตั้งแต่ 2 เป็นต้นไป
            </p>
          </div>
        </div>

        <div className="p-6 border-t bg-gray-50 flex justify-end space-x-3">
          <button
            onClick={onClose}
            disabled={saving}
            className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50"
          >
            ยกเลิก
          </button>
          <button
            onClick={handleSave}
            disabled={saving}
            className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {saving ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                กำลังบันทึก...
              </>
            ) : (
              <>
                <Save className="w-4 h-4 mr-2" />
                บันทึก
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default UnitStructureManagementPage;
