// src/config.js
const isDevelopment = window.location.hostname === 'localhost';

// --- START: กำหนด API URL ตามสภาพแวดล้อม (Development/Production) ---
const API_BASE_URL = isDevelopment
  ? "http://localhost/court_marshal/api" // <-- URL สำหรับตอนพัฒนาบนเครื่องตัวเอง
  : "https://courtmarshal.rf.gd/api";       // <-- URL สำหรับตอนใช้งานจริง
// --- END ---

export const CONFIG = {
  // LINE Configuration
  LINE_CLIENT_ID: process.env.REACT_APP_LINE_CLIENT_ID || "2007949740",

  // API URLs (ย้ายทั้งหมดมารวมกันตรงนี้เพื่อความชัดเจน)
  API_BASE_URL: API_BASE_URL,
  OFFICERS_API: `${API_BASE_URL}/officers.php`,
  UNITS_API: `${API_BASE_URL}/units.php`, // <-- เพิ่มตัวนี้ที่ขาดไป

  // Redirect URI
  REDIRECT_URI: isDevelopment
    ? (process.env.REACT_APP_LINE_REDIRECT_URI_DEV || "http://localhost:3000")
    : (process.env.REACT_APP_LINE_REDIRECT_URI_PROD || "https://courtmarshal.rf.gd"),
};
