import React, { useState, useEffect, useMemo, memo } from 'react';
import { 
  ChevronLeft, 
  ChevronRight, 
  Calendar, 
  Filter, 
  Plus, 
  Shield, 
  Save, 
  Edit, 
  Trash2, 
  Search, 
  X, 
  Moon, 
  Sun, 
  Users, 
  Briefcase,
  Crown,
  Star,
  User,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { CONFIG } from '../config';
import UnitStructureManagement from '../components/UnitStructure/UnitStructureManagement';

// Helper functions
const getDaysInMonth = (date) => new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
const getFirstDayOfMonth = (date) => new Date(date.getFullYear(), date.getMonth(), 1).getDay();
const isToday = (date, today) => date.toDateString() === today.toDateString();
const isSameDay = (date1, date2) => date1.toDateString() === date2.toDateString();

// ฟังก์ชันตรวจสอบวันหยุด
const isHoliday = (date, holidays = []) => {
  // วันเสาร์ (6) และวันอาทิตย์ (0) เป็นวันหยุดตายตัว
  const dayOfWeek = date.getDay();
  if (dayOfWeek === 0 || dayOfWeek === 6) return true;
  
  // ตรวจสอบวันหยุดพิเศษ
  return holidays.some(holiday => isSameDay(new Date(holiday.date + 'T00:00:00'), date));
};

// ฟังก์ชันหาหัวหน้าเวรจากลำดับอาวุโส
const getDutyHead = (officers, selectedUnitCodes) => {
  if (!Array.isArray(officers) || selectedUnitCodes.length === 0) return null;
  
  // หาคนที่อาวุโสที่สุดจากส่วนงานที่เลือก
  const availableOfficers = officers.filter(officer => 
    officer.unit_code && selectedUnitCodes.includes(officer.unit_code)
  );
  
  if (availableOfficers.length === 0) return null;
  
  // เรียงตามลำดับอาวุโส (seniority_order น้อยที่สุด = อาวุโสที่สุด)
  const seniorOfficer = availableOfficers.reduce((senior, current) => 
    current.seniority_order < senior.seniority_order ? current : senior
  );
  
  return seniorOfficer.unit_code;
};

const getRoleIcon = (role) => {
  switch (role) {
    case 'director': return <Crown className="w-4 h-4 text-purple-600" />;
    case 'supervisor': return <Star className="w-4 h-4 text-blue-600" />;
    default: return <User className="w-4 h-4 text-green-600" />;
  }
};

const getRoleLabel = (role) => {
  switch (role) {
    case 'director': return 'ผู้อำนวยการ';
    case 'supervisor': return 'หัวหน้าส่วน';
    default: return 'เจ้าหน้าที่';
  }
};

const getRoleBadgeClass = (role) => {
  switch (role) {
    case 'director': return 'bg-purple-100 text-purple-700';
    case 'supervisor': return 'bg-blue-100 text-blue-700';
    default: return 'bg-green-100 text-green-700';
  }
};

const CalendarDayCell = memo(({ date, holidays, schedules, onAdd, onEdit }) => {
  const today = new Date();
  const isHolidayDate = isHoliday(date, holidays);
  const daySchedules = schedules.filter(schedule => isSameDay(new Date(schedule.duty_date + 'T00:00:00'), date));

  return (
    <div 
      className={`min-h-[120px] border border-slate-200 p-1 cursor-pointer hover:bg-slate-50 ${
        isToday(date, today) ? 'bg-indigo-50 border-indigo-300' : ''
      } ${isHolidayDate ? 'bg-red-50' : ''}`}
      onClick={() => onAdd(date)}
    >
      <div className="flex justify-between items-start mb-1">
        <span className={`text-sm font-medium ${
          isToday(date, today) ? 'text-indigo-600' : 
          isHolidayDate ? 'text-red-600' : 'text-slate-700'
        }`}>
          {date.getDate()}
        </span>
        {isHolidayDate && <span className="text-xs text-red-500">หยุด</span>}
      </div>
      
      <div className="space-y-1">
        {daySchedules.map(schedule => (
          <ScheduleItem key={schedule.duty_id} schedule={schedule} onEdit={onEdit} />
        ))}
      </div>
    </div>
  );
});

const ScheduleItem = memo(({ schedule, onEdit }) => (
  <div 
    className={`text-xs p-1 rounded truncate cursor-pointer ${
      schedule.shift_type === 'day' 
        ? 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200' 
        : 'bg-indigo-100 text-indigo-800 hover:bg-indigo-200'
    }`}
    onClick={(e) => { e.stopPropagation(); onEdit(schedule); }}
    title={`${schedule.shift_type === 'day' ? 'กลางวัน' : 'กลางคืน'} - ${schedule.officer_name || 'ไม่ระบุเจ้าหน้าที่'}`}
  >
    <div className="flex items-center space-x-1">
      {schedule.shift_type === 'day' ? <Sun className="w-3 h-3" /> : <Moon className="w-3 h-3" />}
      <span>{schedule.officer_name || 'ไม่ระบุ'}</span>
    </div>
  </div>
));

const ShiftCalendarView = ({ 
  currentDate, 
  schedules, 
  officers, 
  units, 
  holidays, 
  filters, 
  setFilters, 
  navigateMonth, 
  goToToday, 
  openAddModal, 
  openEditModal 
}) => {
  const daysInMonth = getDaysInMonth(currentDate);
  const firstDay = getFirstDayOfMonth(currentDate);
  const today = new Date();
  
  const monthDays = useMemo(() => {
    const days = [];
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(currentDate.getFullYear(), currentDate.getMonth(), i));
    }
    return days;
  }, [currentDate, daysInMonth]);

  const emptyDays = Array.from({ length: firstDay }, (_, i) => i);

  return (
    <div className="space-y-4">
      {/* Calendar Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <button 
              onClick={() => navigateMonth(-1)}
              className="p-2 hover:bg-slate-100 rounded-lg transition-colors"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>
            <h2 className="text-xl font-semibold text-slate-800 min-w-[200px] text-center">
              {currentDate.toLocaleDateString('th-TH', { year: 'numeric', month: 'long' })}
            </h2>
            <button 
              onClick={() => navigateMonth(1)}
              className="p-2 hover:bg-slate-100 rounded-lg transition-colors"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </div>
          <button 
            onClick={goToToday}
            className="px-3 py-2 text-sm bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
          >
            วันนี้
          </button>
        </div>
        
        {/* Filters */}
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-slate-500" />
            <select 
              value={filters.unit}
              onChange={(e) => setFilters(prev => ({ ...prev, unit: e.target.value }))}
              className="text-sm border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              <option value="">ทุกส่วนงาน</option>
              {units.map(unit => (
                <option key={unit.id} value={unit.id}>{unit.unit_name}</option>
              ))}
            </select>
          </div>
          
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
            <input
              type="text"
              placeholder="ค้นหาเจ้าหน้าที่..."
              value={filters.officer}
              onChange={(e) => setFilters(prev => ({ ...prev, officer: e.target.value }))}
              className="pl-9 pr-3 py-2 text-sm border border-slate-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
          </div>
        </div>
      </div>

      {/* Calendar Grid */}
      <div className="bg-white rounded-lg border border-slate-200 overflow-hidden">
        {/* Days of week header */}
        <div className="grid grid-cols-7 bg-slate-50 border-b border-slate-200">
          {['อาทิตย์', 'จันทร์', 'อังคาร', 'พุธ', 'พฤหัสบดี', 'ศุกร์', 'เสาร์'].map(day => (
            <div key={day} className="p-3 text-center text-sm font-medium text-slate-600">
              {day}
            </div>
          ))}
        </div>
        
        {/* Calendar body */}
        <div className="grid grid-cols-7">
          {/* Empty cells for days before month starts */}
          {emptyDays.map(day => (
            <div key={`empty-${day}`} className="min-h-[120px] border border-slate-200 bg-slate-50"></div>
          ))}
          
          {/* Month days */}
          {monthDays.map(date => (
            <CalendarDayCell
              key={date.toISOString()}
              date={date}
              holidays={holidays}
              schedules={schedules}
              onAdd={openAddModal}
              onEdit={openEditModal}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

const ShiftWorkPage = () => {
  const [activeTab, setActiveTab] = useState('shifts');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSchedule, setSelectedSchedule] = useState(null);
  const [schedules, setSchedules] = useState([]);
  const [officers, setOfficers] = useState([]);
  const [units, setUnits] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [formData, setFormData] = useState({
    duty_date: '',
    officer_id: '',
    dutyhead: '',
    shift_type: 'day',
    day_type: 'normal'
  });
  const [filters, setFilters] = useState({ unit: '', officer: '' });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [selectedShiftPeriod, setSelectedShiftPeriod] = useState('day');
  const [selectedUnits, setSelectedUnits] = useState([]);
  const [selectedHeadUnit, setSelectedHeadUnit] = useState(''); // หน่วยงานหลักที่เป็นหัวหน้าเวร
  const [availableOfficers, setAvailableOfficers] = useState([]); // คนในส่วนงานที่เลือก
  const [alert, setAlert] = useState({ show: false, message: '', type: 'info' });

  // ฟังก์ชันแสดง Alert
  const showAlert = (message, type = 'info') => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ show: false, message: '', type: 'info' }), 5000);
  };

  // Auto-select officers from selected units
  useEffect(() => {
    if (selectedUnits.length > 0 && Array.isArray(officers)) {
      const officersInSelectedUnits = officers.filter(officer => 
        officer.unit_code && selectedUnits.includes(officer.unit_code)
      );
      setAvailableOfficers(officersInSelectedUnits);
    } else {
      setAvailableOfficers([]);
    }
  }, [selectedUnits, officers]);

  // --- ฟังก์ชันจัดการการเลือกส่วนงาน ---
  const handleUnitSelect = (unitCode) => {
    setSelectedUnits(prev => {
      const newUnits = prev.includes(unitCode)
        ? prev.filter(code => code !== unitCode)
        : [...prev, unitCode];
      
      // ล้างการเลือกหน่วยงานหลักถ้าไม่มีส่วนงานที่เลือก
      if (newUnits.length === 0) {
        setSelectedHeadUnit('');
      }
      
      return newUnits;
    });
  };

  const handleHeadUnitSelect = (unitCode) => {
    setSelectedHeadUnit(unitCode);
  };

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  const loadInitialData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadOfficers(),
        loadUnits(),
        loadHolidays(),
        loadSchedules()
      ]);
    } catch (error) {
      console.error('Error loading initial data:', error);
      showAlert('เกิดข้อผิดพลาดในการโหลดข้อมูล', 'error');
    } finally {
      setLoading(false);
    }
  };

  const loadSchedules = async () => {
    try {
      const startDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).toISOString().split('T')[0];
      const endDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).toISOString().split('T')[0];
      
      const response = await fetch(`${CONFIG.DUTY_SCHEDULE_API}?start_date=${startDate}&end_date=${endDate}`);
      const data = await response.json();
      
      if (data.success) {
        setSchedules(data.data || []);
      } else {
        console.error('Failed to fetch schedules:', data.message);
        showAlert('ไม่สามารถโหลดข้อมูลตารางเวรได้', 'error');
      }
    } catch (error) {
      console.error('Error loading schedules:', error);
      showAlert('เกิดข้อผิดพลาดในการโหลดตารางเวร', 'error');
    }
  };

  const loadOfficers = async () => {
    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data && data.success && Array.isArray(data.data)) {
        setOfficers(data.data);
      } else {
        console.error('Failed to load officers:', data);
        setOfficers([]);
      }
    } catch (error) {
      console.error('Error loading officers:', error);
      setOfficers([]);
      showAlert('ไม่สามารถโหลดข้อมูลเจ้าหน้าที่ได้', 'error');
    }
  };

  const loadUnits = async () => {
    try {
      const response = await fetch(`${CONFIG.UNITS_API}`);
      const data = await response.json();
      if (data && data.success && Array.isArray(data.data)) {
        setUnits(data.data);
      } else {
        setUnits([]);
      }
    } catch (error) {
      console.error('Error loading units:', error);
      setUnits([]);
      showAlert('ไม่สามารถโหลดข้อมูลส่วนงานได้', 'error');
    }
  };

  const loadHolidays = async () => {
    try {
      // สำหรับตอนนี้ใช้วันหยุดตายตัว (เสาร์-อาทิตย์)
      // ในอนาคตอาจมีการเพิ่ม API สำหรับวันหยุดพิเศษ
      setHolidays([]);
    } catch (error) {
      console.error('Error loading holidays:', error);
      setHolidays([]);
    }
  };

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(prev.getMonth() + direction);
      return newDate;
    });
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  // --- แก้ไข openAddModal ---
  const openAddModal = (date = null) => {
    const dateObj = date || new Date();
    const isHolidayDate = isHoliday(dateObj, holidays);

    setSelectedShiftPeriod('day');
    setSelectedUnits([]);
    setSelectedHeadUnit('');
    setAvailableOfficers([]);
    setFormData({
      duty_date: date ? date.toISOString().split('T')[0] : '',
      officer_id: '',
      dutyhead: '',
      shift_type: 'day',
      day_type: isHolidayDate ? 'holiday' : 'normal'
    });
    setShowAddModal(true);
  };

  const openEditModal = (schedule) => {
    setSelectedSchedule(schedule);
    setSelectedShiftPeriod(schedule.shift_type);
    
    // หาส่วนงานของเจ้าหน้าที่
    const officer = officers.find(o => o.position_number === schedule.officer_id);
    const unitCodes = officer ? [officer.unit_code] : [];
    
    setSelectedUnits(unitCodes);
    setSelectedHeadUnit(schedule.dutyhead);
    setFormData({
      duty_date: schedule.duty_date,
      officer_id: schedule.officer_id,
      dutyhead: schedule.dutyhead,
      shift_type: schedule.shift_type,
      day_type: schedule.day_type
    });
    setShowEditModal(true);
  };

  const closeModals = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setSelectedSchedule(null);
    setSelectedShiftPeriod('day');
    setSelectedUnits([]);
    setSelectedHeadUnit('');
    setAvailableOfficers([]);
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // ตรวจสอบข้อมูลที่จำเป็น
      if (!formData.duty_date || availableOfficers.length === 0) {
        showAlert('กรุณาระบุวันที่และเลือกส่วนงาน', 'error');
        return;
      }

      if (!selectedHeadUnit) {
        showAlert('กรุณาเลือกหน่วยงานหลักที่เป็นหัวหน้าเวร', 'error');
        return;
      }

      // สร้างเวรสำหรับแต่ละเจ้าหน้าที่ในส่วนงาน
      const promises = availableOfficers.map(async (officer) => {
        const payload = {
          duty_date: formData.duty_date,
          officer_id: officer.position_number,
          dutyhead: selectedHeadUnit,
          shift_type: selectedShiftPeriod,
          day_type: isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) ? 'holiday' : 'normal'
        };

        const url = selectedSchedule ? `${CONFIG.DUTY_SCHEDULE_API}/${selectedSchedule.duty_id}` : CONFIG.DUTY_SCHEDULE_API;
        const method = selectedSchedule ? 'PUT' : 'POST';

        const response = await fetch(url, {
          method,
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        });

        const result = await response.json();
        if (!result.success) {
          throw new Error(result.message || 'เกิดข้อผิดพลาดในการบันทึก');
        }
        return result;
      });

      await Promise.all(promises);
      showAlert('บันทึกตารางเวรสำเร็จ', 'success');
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error saving schedule:', error);
      showAlert(error.message || 'เกิดข้อผิดพลาดในการบันทึก', 'error');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!selectedSchedule || !window.confirm('คุณแน่ใจหรือไม่ที่จะลบตารางเวรนี้?')) return;
    setSaving(true);
    try {
      const response = await fetch(`${CONFIG.DUTY_SCHEDULE_API}/${selectedSchedule.duty_id}`, {
        method: 'DELETE'
      });
      
      const result = await response.json();
      if (!result.success) {
        throw new Error(result.message || 'เกิดข้อผิดพลาดในการลบ');
      }
      
      showAlert('ลบตารางเวรสำเร็จ', 'success');
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error deleting schedule:', error);
      showAlert(error.message || 'เกิดข้อผิดพลาดในการลบ', 'error');
    } finally {
      setSaving(false);
    }
  };

  const filteredSchedules = useMemo(() => {
    return schedules.filter(schedule => {
      const unitMatch = !filters.unit || schedule.officer_unit_code === filters.unit;
      const officerMatch = !filters.officer ||
        (schedule.officer_name && schedule.officer_name.toLowerCase().includes(filters.officer.toLowerCase()));
      return unitMatch && officerMatch;
    });
  }, [schedules, filters]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Alert System */}
      {alert.show && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg ${
          alert.type === 'success' ? 'bg-green-100 text-green-800 border border-green-200' :
          alert.type === 'error' ? 'bg-red-100 text-red-800 border border-red-200' :
          'bg-blue-100 text-blue-800 border border-blue-200'
        }`}>
          <div className="flex items-center">
            {alert.type === 'success' && <CheckCircle className="w-5 h-5 mr-2" />}
            {alert.type === 'error' && <AlertCircle className="w-5 h-5 mr-2" />}
            <span>{alert.message}</span>
            <button
              onClick={() => setAlert({ show: false, message: '', type: 'info' })}
              className="ml-4 text-gray-500 hover:text-gray-700"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
        </div>
      )}

      <div className="border-b border-slate-200">
        <nav className="flex space-x-4 px-4">
          <button
            onClick={() => setActiveTab('shifts')}
            className={`flex items-center space-x-2 py-3 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${activeTab === 'shifts' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
              }`}
          >
            <Shield className="w-4 h-4" />
            <span>เวรรักษาการ</span>
          </button>
          <button
            onClick={() => setActiveTab('documents')}
                className={`flex items-center space-x-2 py-3 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${
                    activeTab === 'documents' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
              }`}
          >
            <Briefcase className="w-4 h-4" />
            <span>จัดการโครงสร้าง</span>
          </button>
        </nav>
      </div>

      <div className="flex-grow p-4 bg-slate-50/50 overflow-y-auto">
        {activeTab === 'shifts' && (
          <ShiftCalendarView
            currentDate={currentDate}
            schedules={filteredSchedules}
            officers={officers}
            units={units}
            holidays={holidays}
            filters={filters}
            setFilters={setFilters}
            navigateMonth={navigateMonth}
            goToToday={goToToday}
            openAddModal={openAddModal}
            openEditModal={openEditModal}
          />
        )}
        {activeTab === 'documents' && <UnitStructureManagement />}
      </div>

      {(showAddModal || showEditModal) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-3xl mx-4 max-h-[90vh] overflow-y-auto">
            <div className="p-6 border-b">
              <h3 className="text-lg font-semibold text-gray-900">
                {showEditModal ? 'แก้ไขตารางเวร' : 'เพิ่มตารางเวรใหม่'}
              </h3>
              {formData.duty_date && (
                <p className="text-sm text-gray-600 mt-1">
                  วันที่ {new Date(formData.duty_date + 'T00:00:00').toLocaleDateString('th-TH')}
                  {isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) &&
                    <span className="ml-2 px-2 py-0.5 bg-red-100 text-red-600 text-xs rounded-full">วันหยุด</span>
                  }
                </p>
              )}
            </div>

            <div className="p-6 space-y-6">
              {/* วันที่ */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">วันที่</label>
                <input
                  type="date"
                  value={formData.duty_date}
                  onChange={(e) => {
                    handleInputChange('duty_date', e.target.value);
                    // อัปเดต shift type อัตโนมัติเมื่อเปลี่ยนวันที่
                    const newDate = new Date(e.target.value + 'T00:00:00');
                    const isHol = isHoliday(newDate, holidays);
                    const suggestedType = getShiftTypeByPeriod(selectedShiftPeriod, isHol, shiftTypes);
                    setFormData(prev => ({ ...prev, shift_type_id: suggestedType?.id || '' }));
                  }}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2"
                />
              </div>

              {/* เลือกช่วงเวร */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">ช่วงเวร</label>
                <div className="flex space-x-3">
                  <button
                    type="button"
                    onClick={() => {
                      setSelectedShiftPeriod('day');
                      const isHol = formData.duty_date ? isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) : false;
                                            const suggestedType = getShiftTypeByPeriod('day', isHol, shiftTypes);
                                            setFormData(prev => ({ ...prev, shift_type_id: suggestedType?.id || '' }));
                    }}
                    className={`flex items-center px-4 py-2 rounded-lg border ${
                        selectedShiftPeriod === 'day' 
                        ? 'bg-yellow-50 border-yellow-300 text-yellow-700'
                        : 'bg-white border-gray-300 text-gray-700 hover:bg-gray-50'
                      }`}
                  >
                    <Sun className="w-4 h-4 mr-2" />
                    กลางวัน
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setSelectedShiftPeriod('night');
                      const suggestedType = getShiftTypeByPeriod('night', false, shiftTypes);
                      setFormData(prev => ({ ...prev, shift_type_id: suggestedType?.id || '' }));
                    }}
                    className={`flex items-center px-4 py-2 rounded-lg border ${
                        selectedShiftPeriod === 'night' 
                        ? 'bg-indigo-50 border-indigo-300 text-indigo-700'
                        : 'bg-white border-gray-300 text-gray-700 hover:bg-gray-50'
                      }`}
                  >
                    <Moon className="w-4 h-4 mr-2" />
                    กลางคืน
                  </button>
                </div>
              </div>

              {/* เลือกส่วนงาน (เช็คลิส) */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">ส่วนงาน</label>
                <div className="border border-gray-300 rounded-lg p-4 max-h-40 overflow-y-auto">
                  <div className="grid grid-cols-2 gap-2">
                    {Array.isArray(units) && units.map(unit => (
                      <label key={unit.id} className="flex items-center py-1">
                        <input
                          type="checkbox"
                                    checked={selectedUnits.includes(unit.id)}
                                    onChange={() => handleUnitSelect(unit.id)}
                                    className="h-4 w-4 text-indigo-600"
                        />
                        <span className="ml-2 text-sm text-gray-900">{unit.unit_name}</span>
                      </label>
                    ))}
                  </div>
                </div>
              </div>

              {/* เจ้าหน้าที่ที่เลือกแล้ว */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">เจ้าหน้าที่ที่เลือก</label>
                <div className="border border-gray-300 rounded-lg p-3 min-h-[100px]">
                  {selectedOfficers.length === 0 ? (
                    <p className="text-gray-500 text-sm">ยังไม่ได้เลือกเจ้าหน้าที่</p>
                  ) : (
                    <div className="space-y-2">
                      {selectedOfficers.map(officerId => {
                        const officer = getOfficerById(officerId);
                        return officer ? (
                          <div key={officerId} className="flex items-center justify-between bg-gray-50 px-3 py-2 rounded-lg">
                            <span className="text-sm">{officer.prefix} {officer.firstname} {officer.lastname}</span>
                            <button
                              onClick={() => handleOfficerRemove(officerId)}
                              className="text-red-600 hover:text-red-800"
                            >
                              <X className="w-4 h-4" />
                            </button>
                          </div>
                        ) : null;
                      })}
                    </div>
                  )}
                </div>
              </div>

              {/* เพิ่มเจ้าหน้าที่ */}
              <div>
                <div className="flex items-center justify-between mb-3">
                  <label className="block text-sm font-medium text-gray-700">จัดการเจ้าหน้าที่</label>
                  <button
                    type="button"
                    onClick={() => setShowOfficerList(!showOfficerList)}
                    className="text-sm text-indigo-600 hover:text-indigo-800"
                  >
                    {showOfficerList ? 'ซ่อน' : 'เพิ่มเจ้าหน้าที่'}
                  </button>
                </div>

                {showOfficerList && (
                  <div className="border border-gray-300 rounded-lg p-3">
                    {/* ช่องค้นหา */}
                    <div className="mb-3">
                      <div className="flex items-center space-x-2">
                        <Search className="w-4 h-4 text-gray-500" />
                        <input
                          type="text"
                          placeholder="ค้นหาชื่อเจ้าหน้าที่..."
                          value={officerSearchTerm}
                          onChange={(e) => setOfficerSearchTerm(e.target.value)}
                          className="flex-1 border border-gray-300 rounded px-2 py-1 text-sm"
                        />
                      </div>
                    </div>

                    {/* รายชื่อเจ้าหน้าที่ */}
                    <div className="max-h-40 overflow-y-auto">
                      {getAvailableOfficers().length === 0 ? (
                        <p className="text-gray-500 text-sm">
                          {selectedUnits.length === 0 ? 'กรุณาเลือกส่วนงานก่อน' :
                            officerSearchTerm ? 'ไม่พบเจ้าหน้าที่ที่ค้นหา' : 'ไม่มีเจ้าหน้าที่ที่สามารถเลือกได้'}
                                        </p>
                      ) : (
                        <div className="space-y-1">
                          {getAvailableOfficers().map(officer => (
                            <div key={officer.id} className="flex items-center justify-between py-2 hover:bg-gray-50 px-2 rounded">
                              <span className="text-sm">{officer.prefix} {officer.firstname} {officer.lastname}</span>
                              <button
                                onClick={() => {
                                  handleOfficerAdd(officer.id);
                                  setOfficerSearchTerm(''); // ล้างค่าค้นหาหลังเพิ่ม
                                }}
                                className="text-indigo-600 hover:text-indigo-800 p-1 rounded hover:bg-indigo-50"
                              >
                                <Plus className="w-4 h-4" />
                              </button>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>

              {/* หมายเหตุ */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">หมายเหตุ</label>
                <textarea
                  value={formData.notes}
                  onChange={(e) => handleInputChange('notes', e.target.value)}
                  rows={3}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2"
                  placeholder="รายละเอียดเพิ่มเติม..."
                />
              </div>
            </div>

            <div className="p-6 border-t bg-gray-50 flex justify-between">
              <div>
                {showEditModal && (
                  <button
                    onClick={handleDelete}
                    disabled={saving}
                    className="inline-flex items-center px-4 py-2 border border-red-300 text-sm font-medium rounded-lg text-red-700 bg-red-50 hover:bg-red-100"
                  >
                    <Trash2 className="w-4 h-4 mr-2" />ลบเวร
                  </button>
                )}
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={closeModals}
                  disabled={saving}
                  className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50"
                >
                  ยกเลิก
                </button>
                <button
                  onClick={handleSave}
                  disabled={saving || selectedOfficers.length === 0}
                  className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {saving ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      กำลังบันทึก...
                    </>
                  ) : (
                    <>
                      <Save className="w-4 h-4 mr-2" />บันทึก
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShiftWorkPage;
