import React from 'react';
import { Menu, Settings } from 'lucide-react';

const Topbar = ({ 
  appName = "CourtMarshalConnect", 
  currentPage = "หน้าหลัก", 
  isMobile = false,
  onMenuClick,
  onSettingsClick,
  children 
}) => {
  return (
    <div className="bg-white/95 backdrop-blur-md border-b border-blue-100 shadow-sm">
      <div className="flex items-center justify-between px-4 py-3">
        {/* Left side - Logo/App Name */}
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-blue-700 rounded-lg flex items-center justify-center">
            <div className="w-5 h-5 bg-yellow-400 rounded-sm"></div>
          </div>
          <div className="flex flex-col">
            <h1 className="text-lg font-bold text-blue-900 leading-tight">
              {isMobile ? appName : appName}
            </h1>
            {isMobile && currentPage && (
              <p className="text-xs text-blue-600 leading-tight">
                {currentPage}
              </p>
            )}
          </div>
        </div>

        {/* Right side - Actions */}
        <div className="flex items-center space-x-2">
          {children}
          
          {isMobile && (
            <button
              onClick={onSettingsClick}
              className="p-1.5 text-blue-700 hover:text-blue-900 hover:bg-blue-100 rounded-lg transition-colors"
              aria-label="ตั้งค่า"
            >
              <Settings className="w-5 h-5" />
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Topbar;
