import React, { useState, useEffect, useMemo, memo } from 'react';
import {
  ChevronLeft,
  ChevronRight,
  Calendar,
  Plus,
  Sun,
  Moon,
} from 'lucide-react';
import { CONFIG } from '../config';

const ShiftWorkPage = ({ schedules, officers, holidays, onAdd, onEdit }) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const daysInMonth = useMemo(() => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
    return date.getDate();
  }, [currentDate]);

  const firstDayOfMonth = useMemo(() => {
    const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
    return date.getDay();
  }, [currentDate]);

  const handlePrevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 1));
  };

  const renderCalendar = () => {
    const days = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-cell empty" />);
    }

    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
      const isHoliday = holidays.some(holiday => new Date(holiday.date).toDateString() === date.toDateString());
      const daySchedules = schedules.filter(schedule => new Date(schedule.duty_date).toDateString() === date.toDateString());

      days.push(
        <div
          key={day}
          className={`calendar-cell ${isHoliday ? 'holiday' : ''}`}
          onClick={() => onAdd(date)}
        >
          <div className="date-header">
            <span>{day}</span>
            {isHoliday && <span className="holiday-label">Holiday</span>}
          </div>
          <div className="schedule-list">
            {daySchedules.map(schedule => (
              <div
                key={schedule.duty_id}
                className={`schedule-item ${schedule.shift_type}`}
                onClick={(e) => {
                  e.stopPropagation();
                  onEdit(schedule);
                }}
              >
                {schedule.shift_type === 'day' ? <Sun /> : <Moon />} {schedule.officer_name || 'Unassigned'}
              </div>
            ))}
          </div>
        </div>
      );
    }

    return days;
  };

  return (
    <div className="shift-work-page">
      <header className="page-header">
        <h1>Shift Work Management</h1>
        <div className="navigation">
          <button onClick={handlePrevMonth}><ChevronLeft /></button>
          <span>{currentDate.toLocaleString('default', { month: 'long', year: 'numeric' })}</span>
          <button onClick={handleNextMonth}><ChevronRight /></button>
        </div>
      </header>
      <div className="calendar-grid">
        {renderCalendar()}
      </div>
    </div>
  );
};

export default ShiftWorkPage;
