import React, { useState, useEffect, useMemo, memo } from 'react';
import { 
  ChevronLeft, 
  ChevronRight, 
  Calendar, 
  Filter, 
  Plus, 
  Shield, 
  Save, 
  Edit, 
  Trash2, 
  Search, 
  X, 
  Moon, 
  Sun, 
  Users, 
  Briefcase,
  Crown,
  Star,
  User,
  AlertCircle
} from 'lucide-react';
import { CONFIG } from '../config';
import UnitStructureManagement from '../components/UnitStructure/UnitStructureManagement';

// Helper functions
const getDaysInMonth = (date) => new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
const getFirstDayOfMonth = (date) => new Date(date.getFullYear(), date.getMonth(), 1).getDay();
const isToday = (date, today) => date.toDateString() === today.toDateString();
const isSameDay = (date1, date2) => date1.toDateString() === date2.toDateString();
const isHoliday = (date, holidays) => holidays.some(holiday => isSameDay(new Date(holiday.date + 'T00:00:00'), date));

// à¸Ÿà¸±à¸‡à¸à¹Œà¸Šà¸±à¸™à¸ªà¸³à¸«à¸£à¸±à¸šà¸à¸²à¸£à¸ˆà¸±à¸”à¸à¸²à¸£à¹€à¸§à¸£
const getShiftTypeForPeriod = (period, isHolidayDate) => {
  if (period === 'day') {
    return isHolidayDate ? 'holiday' : 'normal';
  } else {
    return 'normal'; // à¹€à¸§à¸£à¸à¸¥à¸²à¸‡à¸„à¸·à¸™à¹ƒà¸Šà¹‰ normal à¹€à¸ªà¸¡à¸­
  }
};

// à¸Ÿà¸±à¸‡à¸à¹Œà¸Šà¸±à¸™à¸«à¸²à¸«à¸±à¸§à¸«à¸™à¹‰à¸²à¹€à¸§à¸£à¸ˆà¸²à¸à¸¥à¸³à¸”à¸±à¸šà¸­à¸²à¸§à¸¸à¹‚à¸ª
const getDutyHead = (officers, selectedUnitCodes) => {
  if (!Array.isArray(officers) || selectedUnitCodes.length === 0) return null;
  
  // à¸à¸£à¸­à¸‡à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¸­à¸¢à¸¹à¹ˆà¹ƒà¸™à¸ªà¹ˆà¸§à¸™à¸‡à¸²à¸™à¸—à¸µà¹ˆà¹€à¸¥à¸·à¸­à¸
  const eligibleOfficers = officers.filter(officer => 
    officer.unit_code && selectedUnitCodes.includes(officer.unit_code)
  );
  
  if (eligibleOfficers.length === 0) return null;
  
  // à¹€à¸£à¸µà¸¢à¸‡à¸•à¸²à¸¡à¸¥à¸³à¸”à¸±à¸šà¸­à¸²à¸§à¸¸à¹‚à¸ª (rank_order à¸™à¹‰à¸­à¸¢à¸—à¸µà¹ˆà¸ªà¸¸à¸” = à¸­à¸²à¸§à¸¸à¹‚à¸ªà¸—à¸µà¹ˆà¸ªà¸¸à¸”)
  const sortedOfficers = eligibleOfficers.sort((a, b) => {
    if (a.rank_order !== b.rank_order) {
      return a.rank_order - b.rank_order;
    }
    // à¸–à¹‰à¸²à¸£à¸°à¸”à¸±à¸šà¹€à¸”à¸µà¸¢à¸§à¸à¸±à¸™ à¹€à¸£à¸µà¸¢à¸‡à¸•à¸²à¸¡ seniority_order
    return a.seniority_order - b.seniority_order;
  });
  
  return sortedOfficers[0].unit_code; // return unit_code à¸‚à¸­à¸‡à¸«à¸±à¸§à¸«à¸™à¹‰à¸²à¹€à¸§à¸£
};

const getRoleIcon = (role) => {
  switch (role) {
    case 'director': return <Crown className="w-4 h-4 text-purple-600" />;
    case 'supervisor': return <Star className="w-4 h-4 text-blue-600" />;
    default: return <User className="w-4 h-4 text-green-600" />;
  }
};

const getRoleLabel = (role) => {
  switch (role) {
    case 'director': return 'à¸œà¸¹à¹‰à¸­à¸³à¸™à¸§à¸¢à¸à¸²à¸£';
    case 'supervisor': return 'à¸«à¸±à¸§à¸«à¸™à¹‰à¸²à¸ªà¹ˆà¸§à¸™';
    default: return 'à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ';
  }
};

const getRoleBadgeClass = (role) => {
  switch (role) {
    case 'director': return 'bg-purple-100 text-purple-700';
    case 'supervisor': return 'bg-blue-100 text-blue-700';
    default: return 'bg-green-100 text-green-700';
  }
};

const CalendarDayCell = memo(({ date, holidays, schedules, onAdd, onEdit }) => {
  const today = new Date();
  const isHolidayDate = isHoliday(date, holidays);
  const daySchedules = schedules.filter(schedule => 
    isSameDay(new Date(schedule.duty_date + 'T00:00:00'), date)
  );

  return (
    <div 
      className={`min-h-[120px] border border-slate-200 p-1 cursor-pointer hover:bg-slate-50 ${
        isToday(date, today) ? 'bg-indigo-50 border-indigo-300' : ''
      } ${isHolidayDate ? 'bg-red-50' : ''}`}
      onClick={() => onAdd(date)}
    >
      <div className="flex justify-between items-start mb-1">
        <span className={`text-sm font-medium ${
          isToday(date, today) ? 'text-indigo-600' : 
          isHolidayDate ? 'text-red-600' : 'text-slate-700'
        }`}>
          {date.getDate()}
        </span>
        {isHolidayDate && <span className="text-xs text-red-500">à¸«à¸¢à¸¸à¸”</span>}
      </div>
      
      <div className="space-y-1">
        {daySchedules.map(schedule => (
          <ScheduleItem key={schedule.duty_id} schedule={schedule} onEdit={onEdit} />
        ))}
      </div>
    </div>
  );
});

const ScheduleItem = memo(({ schedule, onEdit }) => (
  <div 
    className="text-xs p-1 bg-indigo-100 text-indigo-800 rounded truncate cursor-pointer hover:bg-indigo-200"
    onClick={(e) => { e.stopPropagation(); onEdit(schedule); }}
