        return;
      }

      // à¸ªà¸£à¹‰à¸²à¸‡à¹€à¸§à¸£à¸ªà¸³à¸«à¸£à¸±à¸šà¹à¸•à¹ˆà¸¥à¸°à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¹€à¸¥à¸·à¸­à¸
      const promises = selectedOfficers.map(async (officerId) => {
        const payload = {
          duty_date: formData.duty_date,
          officer_id: officerId,
          dutyhead: dutyhead,
          shift_type: selectedShiftPeriod,
          day_type: getShiftTypeForPeriod(selectedShiftPeriod, isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays))
        };

        const url = selectedSchedule ? `/api/duty_schedule/${selectedSchedule.duty_id}` : '/api/duty_schedule';
        const method = selectedSchedule ? 'PUT' : 'POST';

        const response = await fetch(url, {
          method,
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        });

        const result = await response.json();
        if (!result.success) {
          throw new Error(result.message || 'à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”à¹ƒà¸™à¸à¸²à¸£à¸šà¸±à¸™à¸—à¸¶à¸');
        }
        return result;
      });

      await Promise.all(promises);
      showAlert('à¸šà¸±à¸™à¸—à¸¶à¸à¸•à¸²à¸£à¸²à¸‡à¹€à¸§à¸£à¸ªà¸³à¹€à¸£à¹‡à¸ˆ', 'success');
