              </div>

              {/* à¹€à¸žà¸´à¹ˆà¸¡à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ */}
              <div>
                <div className="flex items-center justify-between mb-3">
                  <label className="block text-sm font-medium text-gray-700">à¸ˆà¸±à¸”à¸à¸²à¸£à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ</label>
                  <button
                    type="button"
                    onClick={() => setShowOfficerList(!showOfficerList)}
                    className="text-sm text-indigo-600 hover:text-indigo-800"
                  >
                    {showOfficerList ? 'à¸‹à¹ˆà¸­à¸™' : 'à¹€à¸žà¸´à¹ˆà¸¡à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ'}
                  </button>
                </div>

                {showOfficerList && (
                  <div className="border border-gray-300 rounded-lg p-3">
                    {/* à¸Šà¹ˆà¸­à¸‡à¸„à¹‰à¸™à¸«à¸² */}
                    <div className="mb-3">
                      <div className="flex items-center space-x-2">
                        <Search className="w-4 h-4 text-gray-500" />
                        <input
                          type="text"
                          placeholder="à¸„à¹‰à¸™à¸«à¸²à¸Šà¸·à¹ˆà¸­à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ..."
                          value={officerSearchTerm}
                          onChange={(e) => setOfficerSearchTerm(e.target.value)}
                          className="flex-1 border border-gray-300 rounded px-2 py-1 text-sm"
                        />
                      </div>
                    </div>

                    {/* à¸£à¸²à¸¢à¸Šà¸·à¹ˆà¸­à¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆ */}
                    <div className="max-h-40 overflow-y-auto">
                      {getAvailableOfficers().length === 0 ? (
                        <p className="text-gray-500 text-sm">
                          {selectedUnits.length === 0 ? 'à¸à¸£à¸¸à¸“à¸²à¹€à¸¥à¸·à¸­à¸à¸ªà¹ˆà¸§à¸™à¸‡à¸²à¸™à¸à¹ˆà¸­à¸™' :
                            officerSearchTerm ? 'à¹„à¸¡à¹ˆà¸žà¸šà¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¸„à¹‰à¸™à¸«à¸²' : 'à¹„à¸¡à¹ˆà¸¡à¸µà¹€à¸ˆà¹‰à¸²à¸«à¸™à¹‰à¸²à¸—à¸µà¹ˆà¸—à¸µà¹ˆà¸ªà¸²à¸¡à¸²à¸£à¸–à¹€à¸¥à¸·à¸­à¸à¹„à¸”à¹‰'}
                                        </p>
                      ) : (
                        <div className="space-y-1">
