import React, { useState, useEffect, useMemo, memo } from 'react';
import { 
  ChevronLeft, 
  ChevronRight, 
  Calendar, 
  Filter, 
  Plus, 
  Shield, 
  Save, 
  Edit, 
  Trash2, 
  Search, 
  X, 
  Moon, 
  Sun, 
  Users, 
  Briefcase,
  Crown,
  Star,
  User,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { CONFIG } from '../config';
import UnitStructureManagement from '../components/UnitStructure/UnitStructureManagement';

// Helper functions
const getDaysInMonth = (date) => new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
const getFirstDayOfMonth = (date) => new Date(date.getFullYear(), date.getMonth(), 1).getDay();
const isToday = (date, today) => date.toDateString() === today.toDateString();
const isSameDay = (date1, date2) => date1.toDateString() === date2.toDateString();

// ฟังก์ชันตรวจสอบวันหยุด
const isHoliday = (date, holidays = []) => {
  // วันเสาร์ (6) และวันอาทิตย์ (0) เป็นวันหยุดตายตัว
  const dayOfWeek = date.getDay();
  if (dayOfWeek === 0 || dayOfWeek === 6) return true;
  
  // ตรวจสอบวันหยุดพิเศษ
  return holidays.some(holiday => isSameDay(new Date(holiday.date + 'T00:00:00'), date));
};

// ฟังก์ชันหาหัวหน้าเวรจากลำดับอาวุโส
const getDutyHead = (officers, selectedUnitCodes) => {
  if (!Array.isArray(officers) || selectedUnitCodes.length === 0) return null;
  
  // หาคนที่อาวุโสที่สุดจากส่วนงานที่เลือก
  const availableOfficers = officers.filter(officer => 
    officer.unit_code && selectedUnitCodes.includes(officer.unit_code)
  );
  
  if (availableOfficers.length === 0) return null;
  
  // เรียงตามลำดับอาวุโส (seniority_order น้อยที่สุด = อาวุโสที่สุด)
  const seniorOfficer = availableOfficers.reduce((senior, current) => 
    current.seniority_order < senior.seniority_order ? current : senior
  );
  
  return seniorOfficer.unit_code;
};

const getRoleIcon = (role) => {
  switch (role) {
    case 'director': return <Crown className="w-4 h-4 text-purple-600" />;
    case 'supervisor': return <Star className="w-4 h-4 text-blue-600" />;
    default: return <User className="w-4 h-4 text-green-600" />;
  }
};

const getRoleLabel = (role) => {
  switch (role) {
    case 'director': return 'ผู้อำนวยการ';
    case 'supervisor': return 'หัวหน้าส่วน';
    default: return 'เจ้าหน้าที่';
  }
};

const getRoleBadgeClass = (role) => {
  switch (role) {
    case 'director': return 'bg-purple-100 text-purple-700';
    case 'supervisor': return 'bg-blue-100 text-blue-700';
    default: return 'bg-green-100 text-green-700';
  }
};

const CalendarDayCell = memo(({ date, holidays, schedules, onAdd, onEdit }) => {
  const today = new Date();
  const isHolidayDate = isHoliday(date, holidays);
  const daySchedules = schedules.filter(schedule => isSameDay(new Date(schedule.duty_date + 'T00:00:00'), date));
  
  // จัดกลุ่มตามช่วงเวร
  const dayShifts = daySchedules.filter(s => s.shift_type === 'day');
  const nightShifts = daySchedules.filter(s => s.shift_type === 'night');
  
  // นับจำนวนคนในแต่ละช่วง
  const dayCount = dayShifts.length;
  const nightCount = nightShifts.length;
  
  // หาหน่วยงานที่เข้าเวร
  const units = [...new Set(daySchedules.map(s => s.officer_unit_name || s.unit_code).filter(Boolean))];

  return (
    <div 
      className={`min-h-[140px] border border-slate-200 p-2 cursor-pointer transition-all duration-200 hover:shadow-md ${
        isToday(date, today) ? 'bg-indigo-50 border-indigo-300 shadow-sm' : 'hover:bg-slate-50'
      } ${isHolidayDate ? 'bg-red-50 border-red-200' : ''}`}
      onClick={() => onAdd(date)}
    >
      {/* Header ของวัน */}
      <div className="flex justify-between items-start mb-2">
        <span className={`text-sm font-semibold ${
          isToday(date, today) ? 'text-indigo-700' : 
          isHolidayDate ? 'text-red-600' : 'text-slate-800'
        }`}>
          {date.getDate()}
        </span>
        {isHolidayDate && (
          <span className="text-xs bg-red-100 text-red-600 px-1 py-0.5 rounded font-medium">
            หยุด
          </span>
        )}
      </div>
      
      {/* แสดงจำนวนคนเข้าเวรแต่ละช่วง */}
      {(dayCount > 0 || nightCount > 0) && (
        <div className="space-y-1 mb-2">
          {dayCount > 0 && (
            <div className="flex items-center space-x-1">
              <Sun className="w-3 h-3 text-amber-500" />
              <span className="text-xs bg-amber-100 text-amber-700 px-1.5 py-0.5 rounded-full font-medium">
                {dayCount} คน
              </span>
            </div>
          )}
          {nightCount > 0 && (
            <div className="flex items-center space-x-1">
              <Moon className="w-3 h-3 text-indigo-500" />
              <span className="text-xs bg-indigo-100 text-indigo-700 px-1.5 py-0.5 rounded-full font-medium">
                {nightCount} คน
              </span>
            </div>
          )}
        </div>
      )}
      
      {/* แสดงหน่วยงานที่เข้าเวร */}
      {units.length > 0 && (
        <div className="space-y-1">
          {units.slice(0, 2).map((unit, index) => (
            <div key={index} className="text-xs bg-slate-100 text-slate-700 px-1.5 py-0.5 rounded truncate font-medium">
              {unit}
            </div>
          ))}
          {units.length > 2 && (
            <div className="text-xs text-slate-500 font-medium">
              +{units.length - 2} หน่วยงาน
            </div>
          )}
        </div>
      )}
      
      {/* แสดงรายชื่อเจ้าหน้าที่ (แบบย่อ) */}
      <div className="mt-1 space-y-0.5">
        {daySchedules.slice(0, 3).map(schedule => (
          <ScheduleItem key={schedule.duty_id} schedule={schedule} onEdit={onEdit} />
        ))}
        {daySchedules.length > 3 && (
          <div className="text-xs text-slate-500 text-center py-0.5">
            +{daySchedules.length - 3} คน
          </div>
        )}
      </div>
    </div>
  );
});

const ScheduleItem = memo(({ schedule, onEdit }) => (
  <div 
    className={`text-xs p-1 rounded truncate cursor-pointer ${
      schedule.shift_type === 'day' 
        ? 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200' 
        : 'bg-indigo-100 text-indigo-800 hover:bg-indigo-200'
    }`}
    onClick={(e) => { e.stopPropagation(); onEdit(schedule); }}
    title={`${schedule.shift_type === 'day' ? 'กลางวัน' : 'กลางคืน'} - ${schedule.officer_name || 'ไม่ระบุเจ้าหน้าที่'}`}
  >
    <div className="flex items-center space-x-1">
      {schedule.shift_type === 'day' ? <Sun className="w-3 h-3" /> : <Moon className="w-3 h-3" />}
      <span>{schedule.officer_name || 'ไม่ระบุ'}</span>
    </div>
  </div>
));

const ShiftCalendarView = ({ 
  currentDate, 
  schedules, 
  officers, 
  units, 
  holidays, 
  filters, 
  setFilters, 
  navigateMonth, 
  goToToday, 
  openAddModal, 
  openEditModal 
}) => {
  const daysInMonth = getDaysInMonth(currentDate);
  const firstDay = getFirstDayOfMonth(currentDate);
  const today = new Date();
  
  const monthDays = useMemo(() => {
    const days = [];
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(currentDate.getFullYear(), currentDate.getMonth(), i));
    }
    return days;
  }, [currentDate, daysInMonth]);

  const emptyDays = Array.from({ length: firstDay }, (_, i) => i);

  return (
    <div className="space-y-4">
      {/* Calendar Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <button 
              onClick={() => navigateMonth(-1)}
              className="p-2 hover:bg-slate-100 rounded-lg transition-colors"
            >
              <ChevronLeft className="w-5 h-5" />
            </button>
            <h2 className="text-xl font-semibold text-slate-800 min-w-[200px] text-center">
              {currentDate.toLocaleDateString('th-TH', { year: 'numeric', month: 'long' })}
            </h2>
            <button 
              onClick={() => navigateMonth(1)}
              className="p-2 hover:bg-slate-100 rounded-lg transition-colors"
            >
              <ChevronRight className="w-5 h-5" />
            </button>
          </div>
          <button 
            onClick={goToToday}
            className="px-3 py-2 text-sm bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors"
          >
            วันนี้
          </button>
        </div>
        
        {/* Filters */}
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-slate-500" />
            <select 
              value={filters.unit}
              onChange={(e) => setFilters(prev => ({ ...prev, unit: e.target.value }))}
              className="text-sm border border-slate-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              <option value="">ทุกส่วนงาน</option>
              {units.map(unit => (
                <option key={unit.unit_code} value={unit.unit_code}>{unit.unit_name}</option>
              ))}
            </select>
          </div>
          
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400" />
            <input
              type="text"
              placeholder="ค้นหาเจ้าหน้าที่..."
              value={filters.officer}
              onChange={(e) => setFilters(prev => ({ ...prev, officer: e.target.value }))}
              className="pl-9 pr-3 py-2 text-sm border border-slate-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            />
          </div>
        </div>
      </div>

      {/* Calendar Grid */}
      <div className="bg-white rounded-lg border border-slate-200 overflow-hidden">
        {/* Days of week header */}
        <div className="grid grid-cols-7 bg-slate-50 border-b border-slate-200">
          {['อาทิตย์', 'จันทร์', 'อังคาร', 'พุธ', 'พฤหัสบดี', 'ศุกร์', 'เสาร์'].map(day => (
            <div key={day} className="p-3 text-center text-sm font-medium text-slate-600">
              {day}
            </div>
          ))}
        </div>
        
        {/* Calendar body */}
        <div className="grid grid-cols-7">
          {/* Empty cells for days before month starts */}
          {emptyDays.map(day => (
            <div key={`empty-${day}`} className="min-h-[120px] border border-slate-200 bg-slate-50"></div>
          ))}
          
          {/* Month days */}
          {monthDays.map(date => (
            <CalendarDayCell
              key={date.toISOString()}
              date={date}
              holidays={holidays}
              schedules={schedules}
              onAdd={openAddModal}
              onEdit={openEditModal}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

const ShiftWorkPage = () => {
  const [activeTab, setActiveTab] = useState('shifts');
  const [currentDate, setCurrentDate] = useState(new Date());
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedSchedule, setSelectedSchedule] = useState(null);
  const [schedules, setSchedules] = useState([]);
  const [officers, setOfficers] = useState([]);
  const [units, setUnits] = useState([]);
  const [holidays, setHolidays] = useState([]);
  const [formData, setFormData] = useState({
    duty_date: '',
    position_number: '',
    dutyhead: '',
    shift_type: 'day',
    day_type: 'normal'
  });
  const [filters, setFilters] = useState({ unit: '', officer: '' });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [selectedShiftPeriod, setSelectedShiftPeriod] = useState('day');
  const [selectedUnits, setSelectedUnits] = useState([]);
  const [selectedHeadUnit, setSelectedHeadUnit] = useState(''); // หน่วยงานหลักที่เป็นหัวหน้าเวร
  const [availableOfficers, setAvailableOfficers] = useState([]); // คนในส่วนงานที่เลือก
  const [manualAddedOfficers, setManualAddedOfficers] = useState([]); // เพิ่มบุคคลทีละคนโดยค้นหา
  const [removedPositions, setRemovedPositions] = useState([]); // เลือกลบเฉพาะ session นี้
  const [manualSearch, setManualSearch] = useState('');
  const [alert, setAlert] = useState({ show: false, message: '', type: 'info' });

  // ฟังก์ชันแสดง Alert
  const showAlert = (message, type = 'info') => {
    setAlert({ show: true, message, type });
    setTimeout(() => setAlert({ show: false, message: '', type: 'info' }), 5000);
  };

  // Auto-select officers from selected units
  useEffect(() => {
    if (selectedUnits.length > 0 && Array.isArray(officers)) {
      const officersInSelectedUnits = officers.filter(officer => 
        officer.unit_code && selectedUnits.includes(officer.unit_code)
      );
      setAvailableOfficers(officersInSelectedUnits);
    } else {
      setAvailableOfficers([]);
    }
  }, [selectedUnits, officers]);

  // --- ฟังก์ชันจัดการการเลือกส่วนงาน ---
  const handleUnitSelect = (unitCode) => {
    setSelectedUnits(prev => {
      const newUnits = prev.includes(unitCode)
        ? prev.filter(code => code !== unitCode)
        : [...prev, unitCode];
      
      // ล้างการเลือกหน่วยงานหลักถ้าไม่มีส่วนงานที่เลือก
      if (newUnits.length === 0) {
        setSelectedHeadUnit('');
      }
      
      return newUnits;
    });
  };

  const handleHeadUnitSelect = (unitCode) => {
    setSelectedHeadUnit(unitCode);
  };

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  const loadInitialData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadOfficers(),
        loadUnits(),
        loadHolidays(),
        loadSchedules()
      ]);
    } catch (error) {
      console.error('Error loading initial data:', error);
      showAlert('เกิดข้อผิดพลาดในการโหลดข้อมูล', 'error');
    } finally {
      setLoading(false);
    }
  };

  const loadSchedules = async () => {
    try {
      const startDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).toISOString().split('T')[0];
      const endDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).toISOString().split('T')[0];
      const response = await fetch(`${CONFIG.DUTY_SCHEDULE_API}?start_date=${startDate}&end_date=${endDate}`);
      const data = await response.json();

      if (data.success && Array.isArray(data.data)) {
        // Ensure we have officers loaded to enrich schedule rows
        if (!Array.isArray(officers) || officers.length === 0) {
          // loadOfficers will populate `officers` state
          await loadOfficers();
        }

        const officerMap = new Map((officers || []).map(o => [String(o.position_number), o]));

        const enriched = data.data.map(s => {
          const posKey = s.position_number || s.officer_id || s.position_number?.toString();
          const officer = officerMap.get(String(posKey));
          const officerName = officer ? `${officer.prefix ? officer.prefix + ' ' : ''}${officer.firstname || ''} ${officer.lastname || ''}`.trim() : (s.officer_name || null);
          return {
            ...s,
            officer_name: officerName,
            officer_unit_code: officer ? officer.unit_code : (s.officer_unit_code || null),
            officer_unit_name: officer ? officer.unit_name : (s.officer_unit_name || null),
            position_number: s.position_number || s.officer_id || null
          };
        });

        setSchedules(enriched);
      } else {
        console.error('Failed to fetch schedules:', data && data.message);
        showAlert('ไม่สามารถโหลดข้อมูลตารางเวรได้', 'error');
      }
    } catch (error) {
      console.error('Error loading schedules:', error);
      showAlert('เกิดข้อผิดพลาดในการโหลดตารางเวร', 'error');
    }
  };

  const loadOfficers = async () => {
    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data && data.success && Array.isArray(data.data)) {
        setOfficers(data.data);
      } else {
        console.error('Failed to load officers:', data);
        setOfficers([]);
      }
    } catch (error) {
      console.error('Error loading officers:', error);
      setOfficers([]);
      showAlert('ไม่สามารถโหลดข้อมูลเจ้าหน้าที่ได้', 'error');
    }
  };

  const loadUnits = async () => {
    try {
      const response = await fetch(`${CONFIG.UNIT_STRUCTURE_API}`);
      const data = await response.json();
      if (data && data.success && Array.isArray(data.data)) {
        // สกัดข้อมูลส่วนงานที่ไม่ซ้ำจาก unit_structure
        const uniqueUnits = data.data.reduce((acc, officer) => {
          if (officer.unit_code && officer.unit_name) {
            const existingUnit = acc.find(unit => unit.unit_code === officer.unit_code);
            if (!existingUnit) {
              acc.push({
                unit_code: officer.unit_code,
                unit_name: officer.unit_name
              });
            }
          }
          return acc;
        }, []);
        setUnits(uniqueUnits);
      } else {
        setUnits([]);
      }
    } catch (error) {
      console.error('Error loading units:', error);
      setUnits([]);
      showAlert('ไม่สามารถโหลดข้อมูลส่วนงานได้', 'error');
    }
  };

  const loadHolidays = async () => {
    try {
      // สำหรับตอนนี้ใช้วันหยุดตายตัว (เสาร์-อาทิตย์)
      // ในอนาคตอาจมีการเพิ่ม API สำหรับวันหยุดพิเศษ
      setHolidays([]);
    } catch (error) {
      console.error('Error loading holidays:', error);
      setHolidays([]);
    }
  };

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(prev.getMonth() + direction);
      return newDate;
    });
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  // --- แก้ไข openAddModal ---
  const openAddModal = (date = null) => {
    const dateObj = date || new Date();
    const isHolidayDate = isHoliday(dateObj, holidays);

    setSelectedShiftPeriod('day');
    setSelectedUnits([]);
    setSelectedHeadUnit('');
  setAvailableOfficers([]);
  setManualAddedOfficers([]);
  setRemovedPositions([]);
    setFormData({
      duty_date: date ? date.toISOString().split('T')[0] : '',
      position_number: '',
      dutyhead: '',
      shift_type: 'day',
      day_type: isHolidayDate ? 'holiday' : 'normal'
    });
    setShowAddModal(true);
  };

  const openEditModal = (schedule) => {
    setSelectedSchedule(schedule);
    setSelectedShiftPeriod(schedule.shift_type);
    
    // หาส่วนงานของเจ้าหน้าที่
    const officer = officers.find(o => o.position_number === schedule.position_number);
    const unitCodes = officer ? [officer.unit_code] : [];
    
    setSelectedUnits(unitCodes);
    setSelectedHeadUnit(schedule.dutyhead);
    setFormData({
      duty_date: schedule.duty_date,
      position_number: schedule.position_number,
      dutyhead: schedule.dutyhead,
      shift_type: schedule.shift_type,
      day_type: schedule.day_type
    });
    setShowEditModal(true);
  };

  const closeModals = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setSelectedSchedule(null);
    setSelectedShiftPeriod('day');
    setSelectedUnits([]);
    setSelectedHeadUnit('');
    setAvailableOfficers([]);
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // ตรวจสอบข้อมูลที่จำเป็น
      // รวมรายการที่จะบันทึก: available (จากส่วนงาน) + manual
      const combined = [];
      const mapPos = new Map();
      (availableOfficers || []).forEach(o => mapPos.set(String(o.position_number), o));
      (manualAddedOfficers || []).forEach(o => mapPos.set(String(o.position_number), o));
      for (const [pos, off] of mapPos.entries()) {
        if (!removedPositions.includes(pos)) combined.push(off);
      }

      if (!formData.duty_date || combined.length === 0) {
        showAlert('กรุณาระบุวันที่และเลือกเจ้าหน้าที่อย่างน้อย 1 คน', 'error');
        return;
      }

      if (!selectedHeadUnit) {
        showAlert('กรุณาเลือกหน่วยงานหลักที่เป็นหัวหน้าเวร', 'error');
        return;
      }

  // สร้างเวรสำหรับแต่ละเจ้าหน้าที่ที่เลือก (รวม manual)
  const promises = combined.map(async (officer) => {
        const pos = officer.position_number || officer.officer_id || officer.id;
        const posNumber = Number(pos);
        const payload = {
          duty_date: formData.duty_date,
          position_number: Number.isInteger(posNumber) ? posNumber : pos,
          unit_code: officer.unit_code || selectedHeadUnit,
          shift_type: selectedShiftPeriod,
          day_type: isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) ? 'holiday' : 'normal'
        };

        console.log('Payload being sent:', payload); // Debug log

        const url = selectedSchedule ? `${CONFIG.DUTY_SCHEDULE_API}/${selectedSchedule.duty_id}` : CONFIG.DUTY_SCHEDULE_API;
        const method = selectedSchedule ? 'PUT' : 'POST';

        const response = await fetch(url, {
          method,
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload)
        });

        const result = await response.json();
        if (!result.success) {
          throw new Error(result.message || 'เกิดข้อผิดพลาดในการบันทึก');
        }
        return result;
      });

      await Promise.all(promises);
      showAlert('บันทึกตารางเวรสำเร็จ', 'success');
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error saving schedule:', error);
      showAlert(error.message || 'เกิดข้อผิดพลาดในการบันทึก', 'error');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!selectedSchedule || !window.confirm('คุณแน่ใจหรือไม่ที่จะลบตารางเวรนี้?')) return;
    setSaving(true);
    try {
      const response = await fetch(`${CONFIG.DUTY_SCHEDULE_API}/${selectedSchedule.duty_id}`, {
        method: 'DELETE'
      });
      
      const result = await response.json();
      if (!result.success) {
        throw new Error(result.message || 'เกิดข้อผิดพลาดในการลบ');
      }
      
      showAlert('ลบตารางเวรสำเร็จ', 'success');
      await loadSchedules();
      closeModals();
    } catch (error) {
      console.error('Error deleting schedule:', error);
      showAlert(error.message || 'เกิดข้อผิดพลาดในการลบ', 'error');
    } finally {
      setSaving(false);
    }
  };

  const filteredSchedules = useMemo(() => {
    return schedules.filter(schedule => {
      const unitMatch = !filters.unit || schedule.officer_unit_code === filters.unit;
      const officerMatch = !filters.officer ||
        (schedule.officer_name && schedule.officer_name.toLowerCase().includes(filters.officer.toLowerCase()));
      return unitMatch && officerMatch;
    });
  }, [schedules, filters]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Alert System */}
      {alert.show && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg ${
          alert.type === 'success' ? 'bg-green-100 text-green-800 border border-green-200' :
          alert.type === 'error' ? 'bg-red-100 text-red-800 border border-red-200' :
          'bg-blue-100 text-blue-800 border border-blue-200'
        }`}>
          <div className="flex items-center">
            {alert.type === 'success' && <CheckCircle className="w-5 h-5 mr-2" />}
            {alert.type === 'error' && <AlertCircle className="w-5 h-5 mr-2" />}
            <span>{alert.message}</span>
            <button
              onClick={() => setAlert({ show: false, message: '', type: 'info' })}
              className="ml-4 text-gray-500 hover:text-gray-700"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
        </div>
      )}

      <div className="border-b border-slate-200">
        <nav className="flex space-x-4 px-4">
          <button
            onClick={() => setActiveTab('shifts')}
            className={`flex items-center space-x-2 py-3 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${activeTab === 'shifts' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
              }`}
          >
            <Shield className="w-4 h-4" />
            <span>เวรรักษาการ</span>
          </button>
          <button
            onClick={() => setActiveTab('documents')}
                className={`flex items-center space-x-2 py-3 px-3 rounded-t-lg font-medium text-sm whitespace-nowrap ${
                    activeTab === 'documents' ? 'border-b-2 border-indigo-600 text-indigo-600' : 'text-slate-500 hover:text-slate-700'
              }`}
          >
            <Briefcase className="w-4 h-4" />
            <span>จัดการโครงสร้าง</span>
          </button>
        </nav>
      </div>

      <div className="flex-grow p-4 bg-slate-50/50 overflow-y-auto">
        {activeTab === 'shifts' && (
          <ShiftCalendarView
            currentDate={currentDate}
            schedules={filteredSchedules}
            officers={officers}
            units={units}
            holidays={holidays}
            filters={filters}
            setFilters={setFilters}
            navigateMonth={navigateMonth}
            goToToday={goToToday}
            openAddModal={openAddModal}
            openEditModal={openEditModal}
          />
        )}
        {activeTab === 'documents' && <UnitStructureManagement />}
      </div>

      {(showAddModal || showEditModal) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-3xl mx-4 max-h-[90vh] overflow-y-auto">
            <div className="p-6 border-b">
              <h3 className="text-lg font-semibold text-gray-900">
                {showEditModal ? 'แก้ไขตารางเวร' : 'เพิ่มตารางเวรใหม่'}
              </h3>
              {formData.duty_date && (
                <p className="text-sm text-gray-600 mt-1">
                  วันที่ {new Date(formData.duty_date + 'T00:00:00').toLocaleDateString('th-TH')}
                  {isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) &&
                    <span className="ml-2 px-2 py-0.5 bg-red-100 text-red-600 text-xs rounded-full">วันหยุด</span>
                  }
                </p>
              )}
            </div>

            <div className="p-6 space-y-6">
              {/* วันที่ */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">วันที่</label>
                <input
                  type="date"
                  value={formData.duty_date}
                  onChange={(e) => {
                    setFormData(prev => ({ 
                      ...prev, 
                      duty_date: e.target.value,
                      day_type: isHoliday(new Date(e.target.value + 'T00:00:00'), holidays) ? 'holiday' : 'normal'
                    }));
                  }}
                  className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                />
              </div>

              {/* แสดงประเภทวัน */}
              {formData.duty_date && (
                <div className="bg-gray-50 border border-gray-200 rounded-lg p-3">
                  <div className="text-sm text-gray-700">
                    <strong>ประเภทวัน:</strong> {
                      isHoliday(new Date(formData.duty_date + 'T00:00:00'), holidays) 
                        ? <span className="text-red-600">วันหยุด</span>
                        : <span className="text-green-600">วันปกติ</span>
                    }
                  </div>
                </div>
              )}

              {/* เลือกช่วงเวร */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">ช่วงเวร</label>
                <div className="flex space-x-3">
                  <button
                    type="button"
                    onClick={() => {
                      setSelectedShiftPeriod('day');
                      setFormData(prev => ({ ...prev, shift_type: 'day' }));
                    }}
                    className={`flex items-center px-4 py-3 rounded-lg border text-sm font-medium ${
                        selectedShiftPeriod === 'day' 
                        ? 'bg-yellow-50 border-yellow-300 text-yellow-700'
                        : 'bg-white border-gray-300 text-gray-700 hover:bg-gray-50'
                      }`}
                  >
                    <Sun className="w-4 h-4 mr-2" />
                    กลางวัน (08:30-16:30)
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setSelectedShiftPeriod('night');
                      setFormData(prev => ({ ...prev, shift_type: 'night' }));
                    }}
                    className={`flex items-center px-4 py-3 rounded-lg border text-sm font-medium ${
                        selectedShiftPeriod === 'night' 
                        ? 'bg-indigo-50 border-indigo-300 text-indigo-700'
                        : 'bg-white border-gray-300 text-gray-700 hover:bg-gray-50'
                      }`}
                  >
                    <Moon className="w-4 h-4 mr-2" />
                    กลางคืน (16:30-08:30)
                  </button>
                </div>
              </div>

              {/* เลือกส่วนงาน */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  เลือกส่วนงานที่เข้าเวร
                </label>
                <div className="border border-gray-300 rounded-lg p-4 max-h-40 overflow-y-auto">
                  <div className="grid grid-cols-2 gap-2">
                    {Array.isArray(units) && units.map(unit => (
                      <label key={unit.unit_code} className="flex items-center py-1">
                        <input
                          type="checkbox"
                          checked={selectedUnits.includes(unit.unit_code)}
                          onChange={() => handleUnitSelect(unit.unit_code)}
                          className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                        />
                        <span className="ml-2 text-sm text-gray-900">{unit.unit_name}</span>
                      </label>
                    ))}
                  </div>
                </div>
              </div>

              {/* รายชื่อเจ้าหน้าที่ที่จะเข้าเวร */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  รายชื่อเจ้าหน้าที่ที่จะเข้าเวร
                  {availableOfficers.length > 0 && (
                    <span className="text-sm text-blue-600 ml-2">({availableOfficers.length} คน)</span>
                  )}
                </label>
                <div className="border border-gray-300 rounded-lg p-3 max-h-60 overflow-y-auto">
                  {/* Manual search / add single officer */}
                  <div className="mb-3 flex items-center space-x-2">
                    <input
                      type="text"
                      placeholder="ค้นหา/เพิ่มเจ้าหน้าที่ (ระบุหมายเลขตำแหน่งหรือชื่อ)"
                      value={manualSearch}
                      onChange={(e) => setManualSearch(e.target.value)}
                      className="flex-1 border border-gray-300 rounded-lg px-3 py-2 text-sm"
                    />
                    <button
                      type="button"
                      onClick={() => {
                        const q = manualSearch && manualSearch.trim();
                        if (!q) return showAlert('กรุณากรอกข้อมูลเพื่อค้นหา/เพิ่ม', 'error');
                        // หาใน officers จาก unit_structure
                        const found = officers.find(o => String(o.position_number) === q || (`${o.firstname} ${o.lastname}`).toLowerCase().includes(q.toLowerCase()));
                        if (found) {
                          // ถ้ายังไม่มีใน manualAdded หรือ available ให้เพิ่ม
                          const key = String(found.position_number);
                          const already = manualAddedOfficers.find(a => String(a.position_number) === key) || availableOfficers.find(a => String(a.position_number) === key);
                          if (already) {
                            showAlert('เจ้าหน้าที่คนนี้ถูกเพิ่มแล้ว', 'info');
                          } else {
                            setManualAddedOfficers(prev => [...prev, found]);
                            showAlert('เพิ่มเจ้าหน้าที่สำเร็จ', 'success');
                          }
                        } else {
                          showAlert('ไม่พบเจ้าหน้าที่ตามคำค้น', 'error');
                        }
                        setManualSearch('');
                      }}
                      className="px-3 py-2 bg-indigo-600 text-white rounded-lg text-sm"
                    >เพิ่ม</button>
                  </div>

                  {/* Combined list: available + manualAdded (filter removed) */}
                  {((availableOfficers && availableOfficers.length > 0) || (manualAddedOfficers && manualAddedOfficers.length > 0)) ? (
                    <div className="space-y-2">
                      {availableOfficers.map(officer => {
                        const removed = removedPositions.includes(String(officer.position_number));
                        return (
                          <div key={officer.position_number} className={`flex items-center justify-between ${removed ? 'opacity-50 line-through' : ''} bg-green-50 border border-green-200 px-3 py-2 rounded-lg`}>
                            <div className="flex items-center">
                              <CheckCircle className="w-4 h-4 text-green-600 mr-2" />
                              <div>
                                <div className="text-sm font-medium text-green-900">{officer.prefix} {officer.firstname} {officer.lastname}</div>
                                <div className="text-xs text-green-700">({officer.unit_name})</div>
                              </div>
                            </div>
                            <div className="flex items-center space-x-2">
                              <button
                                type="button"
                                onClick={() => {
                                  const key = String(officer.position_number);
                                  setRemovedPositions(prev => prev.includes(key) ? prev.filter(p => p !== key) : [...prev, key]);
                                }}
                                className={`px-2 py-1 text-xs rounded ${removed ? 'bg-yellow-50 text-yellow-800 border border-yellow-200' : 'bg-white text-red-600 border border-red-200'}`}
                              >{removed ? 'ยกเลิกการลบ' : 'ลบ'}</button>
                            </div>
                          </div>
                        );
                      })}

                      {manualAddedOfficers.map(officer => (
                        <div key={`manual-${officer.position_number}`} className="flex items-center justify-between bg-blue-50 border border-blue-200 px-3 py-2 rounded-lg">
                          <div className="flex items-center">
                            <CheckCircle className="w-4 h-4 text-blue-600 mr-2" />
                            <div>
                              <div className="text-sm font-medium text-blue-900">{officer.prefix} {officer.firstname} {officer.lastname}</div>
                              <div className="text-xs text-blue-700">({officer.unit_name})</div>
                            </div>
                          </div>
                          <div>
                            <button
                              type="button"
                              onClick={() => setManualAddedOfficers(prev => prev.filter(a => String(a.position_number) !== String(officer.position_number)))}
                              className="px-2 py-1 text-xs rounded bg-white text-red-600 border border-red-200"
                            >นำออก</button>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-gray-500 text-sm text-center py-4">กรุณาเลือกส่วนงานหรือเพิ่มเจ้าหน้าที่ด้วยตนเอง</p>
                  )}
                </div>
              </div>

              {/* เลือกหน่วยงานหลักที่ดูแลเวร */}
              {availableOfficers.length > 0 && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-3">
                    เลือกหน่วยงานหลักที่ดูแลเวร (หัวหน้าเวร)
                  </label>
                  <select
                    value={selectedHeadUnit}
                    onChange={(e) => setSelectedHeadUnit(e.target.value)}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    required
                  >
                    <option value="">-- เลือกหน่วยงานหลัก --</option>
                    {selectedUnits.map(unitCode => {
                      const unit = units.find(u => u.unit_code === unitCode);
                      return (
                        <option key={unitCode} value={unitCode}>
                          {unit ? unit.unit_name : unitCode}
                        </option>
                      );
                    })}
                  </select>
                  {selectedHeadUnit && (
                    <div className="mt-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                      <div className="text-sm text-blue-800">
                        <strong>หัวหน้าเวร:</strong> {(() => {
                          const headOfficer = availableOfficers.find(o => o.unit_code === selectedHeadUnit);
                          return headOfficer 
                            ? `${headOfficer.prefix} ${headOfficer.firstname} ${headOfficer.lastname} (${headOfficer.unit_name})`
                            : 'ไม่พบเจ้าหน้าที่ในหน่วยงานนี้';
                        })()}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            <div className="p-6 border-t bg-gray-50 flex justify-between">
              <div>
                {showEditModal && (
                  <button
                    onClick={handleDelete}
                    disabled={saving}
                    className="inline-flex items-center px-4 py-2 border border-red-300 text-sm font-medium rounded-lg text-red-700 bg-red-50 hover:bg-red-100 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <Trash2 className="w-4 h-4 mr-2" />ลบเวร
                  </button>
                )}
              </div>
              <div className="flex space-x-3">
                <button
                  onClick={closeModals}
                  disabled={saving}
                  className="px-4 py-2 border border-gray-300 text-sm font-medium rounded-lg text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  ยกเลิก
                </button>
                <button
                  onClick={handleSave}
                  disabled={saving || availableOfficers.length === 0 || !selectedHeadUnit}
                  className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white text-sm font-medium rounded-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {saving ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      กำลังบันทึก...
                    </>
                  ) : (
                    <>
                      <Save className="w-4 h-4 mr-2" />บันทึก
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShiftWorkPage;
