// src/layouts/AppLayout.js
import React, { useState, useEffect, useCallback } from "react";
import { Link, useLocation, Outlet } from "react-router-dom";
import {
  LayoutDashboard, UsersRound, CalendarCheck2, MessagesSquare, CircleUserRound,
  ShieldCheck, ClipboardList, Bell, X, ShieldAlert, Dot, Settings, Menu, ChevronLeft, ChevronRight
} from "lucide-react";
import Topbar from "./components/Topbar";
import logo from "../assets/logo.png";
import favicon from "../assets/favicon.ico";

export default function AppLayout({
  user,
  appName = "CourtMarshalConnect",
  onLogout,
  onOpenNotifications
}) {
  const [showMobileSettings, setShowMobileSettings] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [screenSize, setScreenSize] = useState('mobile');
  const location = useLocation();
  const currentPath = location.pathname;

  // ปรับปรุงการตรวจจับขนาดหน้าจอ
  const handleResize = useCallback(() => {
    const width = window.innerWidth;
    if (width >= 1280) {
      setScreenSize('desktop');
    } else if (width >= 768) {
      setScreenSize('tablet');
    } else {
      setScreenSize('mobile');
    }

    // Auto-close mobile settings on resize
    if (width >= 768) {
      setShowMobileSettings(false);
    }
  }, []);

  useEffect(() => {
    handleResize(); // Set initial state
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [handleResize]);

  const navigationItems = [
    { to: "/", key: "home", label: "หน้าหลัก", icon: LayoutDashboard, description: "ภาพรวมข้อมูลทั้งหมด", badge: null },
    { to: "/personnel", key: "personnel", label: "บุคลากร", icon: UsersRound, description: "จัดการข้อมูลเจ้าหน้าที่", badge: null },
    {
      key: 'duty-management',
      to: '/duty-management',
      label: 'จัดการเวร',
      description: 'ตั้งค่าและดูข้อมูลการเข้าเวร',
      icon: ClipboardList,
    },
    { to: "/tasks", key: "tasks", label: "ตารางเวร", icon: CalendarCheck2, description: "จัดตารางและมอบหมายงาน", badge: null },
    { to: "/chat", key: "chat", label: "แชทหน่วย", icon: MessagesSquare, description: "สื่อสารภายในทีม", badge: null },
    { to: "/profile", key: "profile", label: "โปรไฟล์", icon: CircleUserRound, description: "ข้อมูลส่วนตัวของคุณ", badge: null },
  ];

  if (user && user.role === 'admin') {
    navigationItems.splice(4, 0, {
      to: "/admin",
      key: "admin",
      label: "แผงควบคุม",
      icon: ShieldCheck,
      description: "จัดการระบบสำหรับ Admin",
      badge: null
    });
  }

  const isActive = useCallback((to) => {
    return to === "/" ? currentPath === "/" : currentPath.startsWith(to);
  }, [currentPath]);

  const closeMobileMenus = () => {
    setShowMobileSettings(false);
  };

  const currentPage = navigationItems.find(item => isActive(item.to)) || { label: 'CourtMarshalConnect' };

  // Desktop Layout
  if (screenSize === 'desktop') {
    const sidebarWidth = isSidebarCollapsed ? '80px' : '280px';

    return (
      <div 
        className="flex bg-gradient-to-br from-slate-50 to-slate-100 overflow-hidden" 
        style={{ 
          width: '100vw', 
          height: '100vh',
          position: 'fixed',
          top: 0,
          left: 0,
          margin: 0,
          padding: 0
        }}
      >
        {/* Modern Desktop Sidebar */}
        <aside 
          className={`bg-white shadow-xl border-r border-slate-200/50 flex-shrink-0 overflow-hidden transition-all duration-300 ease-in-out ${
            isSidebarCollapsed ? 'shadow-lg' : 'shadow-2xl'
          }`}
          style={{ width: sidebarWidth, height: '100vh' }}
        >
          {/* Sidebar Header */}
          <div className="relative bg-gradient-to-r from-blue-600 to-blue-700 text-white">
            <div className={`p-6 border-b border-blue-500/30 ${isSidebarCollapsed ? 'p-4' : 'p-6'}`}>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="relative">
                    <img 
                      src={logo} 
                      alt="Logo" 
                      className={`transition-all duration-300 ${
                        isSidebarCollapsed ? 'w-8 h-8' : 'w-10 h-10'
                      } rounded-lg shadow-md`}
                    />
                    <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-400 rounded-full border-2 border-white"></div>
                  </div>
                  {!isSidebarCollapsed && (
                    <div className="transition-opacity duration-300">
                      <h1 className="text-lg font-bold">{appName}</h1>
                      <p className="text-xs text-blue-200 opacity-90">ระบบบริหารจัดการ</p>
                    </div>
                  )}
                </div>
                <button
                  onClick={() => setIsSidebarCollapsed(!isSidebarCollapsed)}
                  className="p-2 hover:bg-blue-600/50 rounded-lg transition-colors duration-200"
                >
                  {isSidebarCollapsed ? (
                    <ChevronRight className="w-5 h-5" />
                  ) : (
                    <ChevronLeft className="w-5 h-5" />
                  )}
                </button>
              </div>
            </div>
          </div>

          {/* User Profile Section */}
          {!isSidebarCollapsed && (
            <div className="p-4 bg-gradient-to-r from-blue-50 to-indigo-50 border-b border-slate-200">
              <div className="flex items-center space-x-3">
                <div className="relative">
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-600 to-blue-700 rounded-full flex items-center justify-center shadow-lg">
                    <CircleUserRound className="w-6 h-6 text-white" />
                  </div>
                  <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-400 rounded-full border-2 border-white"></div>
                </div>
                <div className="min-w-0 flex-1">
                  <h3 className="text-sm font-semibold text-slate-800 truncate">
                    {`${user?.prefix || ''} ${user?.firstname || ''} ${user?.lastname || ''}`.trim()}
                  </h3>
                  <p className="text-xs text-slate-500 truncate">{user?.position || "เจ้าหน้าที่"}</p>
                  {user?.role === 'admin' && (
                    <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800 mt-1">
                      <ShieldCheck className="w-3 h-3 mr-1" />
                      Admin
                    </span>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Navigation Menu */}
          <nav className="p-4 space-y-1 flex-1 overflow-y-auto">
            {navigationItems.map((item, index) => (
              <Link
                key={item.key}
                to={item.to}
                className={`group flex items-center space-x-3 px-4 py-3 rounded-xl transition-all duration-200 relative ${
                  isActive(item.to)
                    ? 'bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg transform scale-[1.02]'
                    : 'text-slate-600 hover:bg-slate-100 hover:text-slate-800 hover:transform hover:scale-[1.01]'
                } ${isSidebarCollapsed ? 'justify-center' : ''}`}
                title={isSidebarCollapsed ? item.label : ''}
              >
                <div className="relative">
                  <item.icon className={`transition-all duration-200 ${
                    isActive(item.to) ? 'w-5 h-5' : 'w-5 h-5 group-hover:scale-110'
                  }`} />
                  {item.badge && (
                    <div className="absolute -top-2 -right-2 w-3 h-3 bg-red-500 rounded-full flex items-center justify-center">
                      <div className="w-2 h-2 bg-white rounded-full"></div>
                    </div>
                  )}
                </div>
                {!isSidebarCollapsed && (
                  <div className="flex-1 min-w-0">
                    <span className="font-medium">{item.label}</span>
                    <p className="text-xs opacity-70 truncate">{item.description}</p>
                  </div>
                )}
                {isActive(item.to) && (
                  <div className="absolute left-0 top-0 bottom-0 w-1 bg-yellow-400 rounded-r-full"></div>
                )}
              </Link>
            ))}
          </nav>

          {/* Sidebar Footer */}
          <div className="border-t border-slate-200 p-4">
            <div className={`flex items-center space-x-3 ${isSidebarCollapsed ? 'justify-center' : ''}`}>
              <Link
                to="/settings"
                className="flex items-center space-x-2 px-3 py-2 text-slate-500 hover:text-slate-700 hover:bg-slate-100 rounded-lg transition-colors"
                title={isSidebarCollapsed ? "ตั้งค่า" : ''}
              >
                <Settings className="w-5 h-5" />
                {!isSidebarCollapsed && <span className="text-sm">ตั้งค่า</span>}
              </Link>
              {!isSidebarCollapsed && (
                <button
                  onClick={onLogout}
                  className="ml-auto px-3 py-2 text-xs font-medium text-red-600 hover:text-red-700 hover:bg-red-50 rounded-lg transition-colors"
                >
                  ออกจากระบบ
                </button>
              )}
            </div>
          </div>
        </aside>

        {/* Desktop Main Content */}
        <div 
          className="flex flex-col bg-slate-50"
          style={{ 
            width: `calc(100vw - ${sidebarWidth})`, 
            height: '100vh',
            overflow: 'hidden'
          }}
        >
          <header className="bg-white/80 backdrop-blur-sm border-b border-slate-200/50 px-6 py-4 flex-shrink-0 shadow-sm">
            <div className="flex justify-between items-center">
              <div className="flex items-center space-x-4">
                <h2 className="text-xl font-semibold text-slate-800">{currentPage.label}</h2>
                <div className="hidden md:flex items-center space-x-2 px-3 py-1 bg-blue-50 text-blue-700 rounded-full text-sm">
                  <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
                  <span>ออนไลน์</span>
                </div>
              </div>
              <div className="flex items-center space-x-4">
                <button
                  onClick={onOpenNotifications}
                  className="relative p-2 text-slate-500 hover:bg-slate-100 rounded-lg transition-colors"
                >
                  <Bell className="w-5 h-5" />
                  <div className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full animate-pulse"></div>
                </button>
                <div className="h-6 w-px bg-slate-300"></div>
                <div className="flex items-center space-x-3">
                  <div className="text-right">
                    <p className="text-sm font-medium text-slate-700">
                      {`${user?.prefix || ''} ${user?.firstname || ''} ${user?.lastname || ''}`.trim()}
                    </p>
                    <p className="text-xs text-slate-500">{user?.department || "หน่วยงาน"}</p>
                  </div>
                  <div className="relative">
                    <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-blue-700 rounded-full flex items-center justify-center shadow-md">
                      <CircleUserRound className="w-5 h-5 text-white" />
                    </div>
                    <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-400 rounded-full border-2 border-white"></div>
                  </div>
                </div>
              </div>
            </div>
          </header>
          <main 
            className="overflow-auto bg-gradient-to-br from-slate-50 to-slate-100"
            style={{ 
              height: 'calc(100vh - 80px)',
              width: '100%'
            }}
          >
            <div className="p-6">
              <Outlet />
            </div>
          </main>
        </div>
      </div>
    );
  }

  // Tablet Layout
  if (screenSize === 'tablet') {
    return (
      <div className="flex min-h-screen w-full bg-slate-50">
        {/* Tablet Sidebar */}
        <aside className="w-20 bg-white border-r border-slate-200 shadow-sm flex-shrink-0">
          <div className="p-4 border-b border-slate-200">
            <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
              <ShieldAlert className="w-5 h-5 text-white" />
            </div>
          </div>
          <nav className="p-2 space-y-2">
            {navigationItems.map((item) => (
              <Link
                key={item.key}
                to={item.to}
                className={`flex flex-col items-center space-y-1 px-2 py-3 rounded-lg transition-colors group ${
                  isActive(item.to)
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-slate-600 hover:bg-slate-100'
                }`}
                title={item.label}
              >
                <item.icon className="w-6 h-6" />
                <span className="text-xs font-medium truncate w-full text-center">
                  {item.label}
                </span>
              </Link>
            ))}
          </nav>
        </aside>

        {/* Tablet Main Content */}
        <div className="flex-1 flex flex-col min-h-screen">
          <header className="bg-white border-b border-slate-200 px-4 py-3 flex-shrink-0">
            <div className="flex justify-between items-center">
              <h2 className="text-lg font-semibold text-slate-800">{currentPage.label}</h2>
              <div className="flex items-center space-x-3">
                <button
                  onClick={onOpenNotifications}
                  className="p-2 text-slate-500 hover:bg-slate-100 rounded-lg"
                >
                  <Bell className="w-5 h-5" />
                </button>
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-slate-600 hidden sm:block">
                    {`${user?.prefix || ''} ${user?.firstname || ''} ${user?.lastname || ''}`.trim()}
                  </span>
                  <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center">
                    <CircleUserRound className="w-5 h-5 text-white" />
                  </div>
                </div>
              </div>
            </div>
          </header>
          <main className="flex-1 p-4 overflow-auto w-full">
            <Outlet />
          </main>
        </div>

        {/* Floating On-Duty Button for Tablet */}
        <Link
          to="/on-duty"
          className="fixed bottom-6 right-6 z-30"
          aria-label="เข้าเวร / รายงานตัว"
        >
          <div className="flex items-center justify-center w-14 h-14 bg-blue-700 text-yellow-400 rounded-xl shadow-lg hover:bg-blue-800 hover:scale-105 transition-transform duration-200">
            <ShieldAlert className="w-6 h-6" />
          </div>
        </Link>
      </div>
    );
  }

  // Mobile Layout
  return (
    <>
      {/* Floating On-Duty Button */}
      <Link
        to="/on-duty"
        className="fixed bottom-20 right-3 z-30"
        aria-label="เข้าเวร / รายงานตัว"
      >
        <div className="flex items-center justify-center w-12 h-12 bg-blue-700 text-yellow-400 rounded-xl shadow-lg active:bg-blue-800 active:scale-95 transition-transform duration-200">
          <ShieldAlert className="w-5 h-5" />
        </div>
      </Link>

      <div className="absolute inset-0 w-full min-h-screen bg-transparent z-0">
        {/* Mobile Topbar */}
        <div className="fixed top-0 left-0 right-0 z-50">
          <Topbar 
            appName={appName} 
            currentPage={currentPage.label} 
            logo={logo}
            isMobile={true}
            onSettingsClick={() => setShowMobileSettings(true)}
          >
            <button
              onClick={onOpenNotifications}
              className="relative p-1.5 text-blue-700 hover:text-blue-900 hover:bg-blue-100 rounded-lg transition-colors"
              aria-label="แจ้งเตือน"
            >
              <Bell className="w-5 h-5" />
            </button>
          </Topbar>
        </div>

        {/* Mobile Settings Panel */}
        {showMobileSettings && (
          <div className="fixed inset-0 bg-black/50 z-50" onClick={closeMobileMenus}>
            <div className="fixed inset-y-0 right-0 w-72 bg-white shadow-2xl rounded-l-xl" onClick={(e) => e.stopPropagation()}>
              <div className="p-3 border-b border-blue-100">
                <div className="flex items-center justify-between">
                  <h2 className="text-base font-semibold text-blue-900">โปรไฟล์</h2>
                  <button
                    onClick={closeMobileMenus}
                    className="p-1.5 text-blue-400 hover:text-blue-700 hover:bg-blue-100 rounded-lg"
                  >
                    <X className="w-4 h-4" />
                  </button>
                </div>
              </div>
              <div className="p-3">
                <div className="flex items-center space-x-3 p-3 bg-blue-50 border border-blue-100 rounded-xl shadow-sm">
                  <div className="w-12 h-12 bg-blue-700 rounded-xl flex items-center justify-center flex-shrink-0">
                    <CircleUserRound className="w-6 h-6 text-yellow-400" />
                  </div>
                  <div className="min-w-0 flex-1">
                    <h3 className="text-sm font-semibold text-blue-900 truncate">
                      {`${user?.prefix || ''} ${user?.firstname || ''} ${user?.lastname || ''}`.trim() || "ผู้ใช้งาน"}
                    </h3>
                    <p className="text-xs text-blue-700 truncate">{user?.position || "เจ้าหน้าที่"}</p>
                    <p className="text-xs text-blue-400 truncate">{user?.phone || "ไม่ระบุ"}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Mobile Main Content */}
        <main className="w-full min-h-screen overflow-auto p-0 m-0" style={{ paddingTop: '3.5rem', paddingBottom: '5rem' }}>
          <div className="px-3 py-2">
            <Outlet />
          </div>
        </main>

        {/* Mobile Bottom Navigation */}
        <nav className="fixed bottom-2 left-2 right-2 bg-white/95 backdrop-blur-md border border-blue-100 rounded-xl shadow-lg px-2 py-1.5 z-40">
          <div className="flex items-center justify-around">
            {navigationItems.slice(0, 5).map((item) => (
              <Link
                key={item.key}
                to={item.to}
                className={`flex flex-col items-center py-1.5 px-2 rounded-lg transition-all duration-200 relative transform active:scale-95 no-underline min-w-0 ${
                  isActive(item.to) ? 'text-blue-700 font-semibold' : 'text-blue-400'
                }`}
              >
                <div className="relative">
                  <item.icon className="w-5 h-5" />
                  {item.badge && (
                    <span className="absolute -top-1 -right-1 w-2 h-2 bg-yellow-400 rounded-full">
                      <Dot className="w-2 h-2 text-blue-900" />
                    </span>
                  )}
                </div>
                <span className={`text-xs font-medium transition-all duration-200 truncate max-w-full ${
                  isActive(item.to) ? 'text-blue-900' : 'text-blue-400'
                }`}> 
                  {item.label}
                </span>
                <div className={`absolute bottom-0 left-1/2 -translate-x-1/2 h-0.5 w-1/2 bg-yellow-400 rounded-full transition-transform duration-300 ease-out ${
                  isActive(item.to) ? 'scale-x-100' : 'scale-x-0'
                }`} />
              </Link>
            ))}
          </div>
        </nav>
      </div>
    </>
  );
}
